INSERT INTO `widok_evo_site_plugins` VALUES ('113','ManagerCss','Customize Evo Manager look','0','4','0','$e = & $modx->Event;\n\nif ( $e->name == \" OnManagerTopPrerender\" ) {\n	\n}\n\nif ( $e->name == \"OnManagerTreePrerender\" ) {\n	$html = \'\n	<style>\n		#treeRoot > div:nth-child(-n+\'.$contextHowMany.\') > .node {\n			padding-top:    5px;\n			padding-bottom: 5px;\n			background-color: rgba(0,0,0,0.03) !important;\n		}\n		#treeRoot > div:nth-child(-n+\'.$contextHowMany.\') > div {\n			padding-top:    5px;\n			padding-bottom: 5px;\n		}\n		.dark #treeRoot > div:nth-child(-n+\'.$contextHowMany.\') > .node,\n		.darkness #treeRoot > div:nth-child(-n+\'.$contextHowMany.\') > .node {\n			background-color: rgba(255,255,255,0.05) !important;\n		}\n	</style>\n	\';\n	$e->output($html);\n}\n\nif ( $e->name == \"OnManagerMainFrameHeaderHTMLBlock\" ) {\n	$html = \'\n	<style>\n		/* show order number in table row in dataTable mode */\n		.multitv .dataTable th.handle {\n			width: 50px !important;\n		}\n		.multitv table.display td.handle {\n			text-indent: 15px !important;\n			background-position: 5px center !important;\n		}\n	</style>\n	\';\n	$e->output($html);\n}','1','{\n  \"contextHowMany\": [\n    {\n      \"label\": \"Number of contexts\",\n      \"type\": \"text\",\n      \"value\": \"6\",\n      \"default\": \"\",\n      \"desc\": \"\"\n    }\n  ]\n}','0',' ','1528753210','1547629292');
