INSERT INTO `widok_evo_site_snippets` VALUES ('157','dl_prepare_archive','','0','69','0','\n$params[\'id\']             = \'archiveIssues\';\n$params[\'parents\']        = $data[\'id\'];\n$params[\'showParent\']     = \'0\';\n$params[\'showNoPublish\']  = \'0\';\n$params[\'depth\']          = \'0\';\n$params[\'orderBy\']        = \'menuindex DESC\';\n$params[\'tvList\']         = \'issueCoverImage,issueCoverInfoShort,issueEditorSelector,issueEditorVariant,issueNumber,issueTitle,issueYear\';\n$params[\'renderTV\']       = \'issueEditors\';\n$params[\'customLang\']     = \'polish-utf8\';\n\n// prepare\n\n$params[\'prepare\'] = function($data, $modx) {\n	// title\n	if ( $data[\'tv.issueTitle\'] ) {\n		$data[\'prepared.issueTitleHtml\'] = \'<span class=\"title\">\'.$data[\'tv.issueTitle\'].\'</span>\';\n		$data[\'prepared.issueNumberColon\'] = \':\';\n	}\n	\n	// editors\n	$editors = $data[\'tv.issueEditorSelector\'];\n	$data[\'prepared.editors\'] = $modx->runSnippet(\'simpleDocs\', [\'docs\' => $editors]);\n\n	// variant\n	$data[\'prepared.variant\'] = $modx->runSnippet(\'getVariant\', [\n		\'type\' => \'editors\',\n		\'variant\' => $data[\'tv.issueEditorVariant\']\n	]);\n\n	// lexicon\n	$data[\'lexicon.issueEditors\'] = $modx->getPlaceholder(\'lexicon.issueEditors\');\n	$data[\'lexicon.coverArtwork\'] = $modx->getPlaceholder(\'lexicon.coverArtwork\');\n	$data[\'lexicon.numberShort\']  = $modx->getPlaceholder(\'lexicon.numberShort\');\n\n	// cover thumb \n	// 1x\n	$data[\'prepared.coverImage_1x\'] = $modx->runSnippet(\'phpthumb\',[\n		\'input\'   => $data[\'tv.issueCoverImage\'], \n		\'options\' => \'w=220,h=135,zc=1,q=80,f=jpg\'\n	]);\n	// 2x\n	$data[\'prepared.coverImage_2x\'] = $modx->runSnippet(\'phpthumb\',[\n		\'input\'   => $data[\'tv.issueCoverImage\'], \n		\'options\' => \'w=440,h=270,zc=1,q=80,f=jpg\'\n	]);\n	// 3x\n	$data[\'prepared.coverImage_3x\'] = $modx->runSnippet(\'phpthumb\',[\n		\'input\'   => $data[\'tv.issueCoverImage\'], \n		\'options\' => \'w=660,