INSERT INTO `widok_evo_site_snippets` VALUES ('156','dl_prepare_issue','','0','69','0','\n// get context\n$context = $modx->getPlaceholder(\'context\');\n\n// authors\n$authors = $data[\'tv.issueArticleAuthorSelector\'];\nif ( !empty($authors) ) {\n	$data[\'prepared.authors\'] = $modx->runSnippet(\'simpleDocs\', [\'docs\' => $authors, \'renderAs\' => \'biolinks\']);\n}\n\n// variant\n$data[\'prepared.variant\'] = $modx->runSnippet(\'getVariant\', [\n	\'type\' => \'authors\',\n	\'variant\' => $data[\'tv.issueArticleAuthorVariant\']\n]);\n\n// render link\n$linkTextPL = \'Czytaj (PL)\';\n$linkTextEN = \'Read (EN)\';\nswitch ($context) {\n	case \'pl\' : $linkText = $linkTextPL; break;\n	case \'en\' : $linkText = $linkTextEN; break;\n}\n$linkUrl = $modx->makeUrl($data[\'id\']);\n$data[\'prepared.link\'] = \'<a href=\"\'.$linkUrl.\'\">\'.$linkText.\'</a>\';\n\n// render link to translation\n$translationId = $data[\'tv.translationSelector\'];\nif ( !empty($translationId) ) {\n	switch ($context) {\n		case \'pl\' : $translationLinkText = $linkTextEN; $translationContext = \'en\'; break;\n		case \'en\' : $translationLinkText = $linkTextPL; $translationContext = \'pl\'; break;\n	}\n	$translationUrl = $modx->makeUrl($translationId);\n	$data[\'prepared.translationLink\'] = \'<a hreflang=\"\'.$translationContext.\'\" href=\"\'.$translationUrl.\'\">\'.$translationLinkText.\'</a>\';\n	$data[\'prepared.itemClass\'] = \'c-issue-toc__article c-issue-toc__article--has-translation\';\n} else {\n	$data[\'prepared.itemClass\'] = \'c-issue-toc__article c-issue-toc__article--no-translation\';\n}\n\nreturn $data;','0','{}',' ','1537703301','1548756804','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('148','run_dl_search','','0','69','0','\nif ( $_GET[\'search\'] == \'true\' ) {\n\n	$context = $modx->getPlaceholder(\'context\');\n\n	// parameters\n\n	$params[\'id\']             = \'search\';\n	$params[\'parents\']        = $modx->getPlaceholder(\'cfg_id_archive\');\n	$params[\'showParent\']     = \'0\';\n	$params[\'depth\']          = \'2\';\n	$params[\'tvList\']         = \'issueArticleAuthor,issueArticleDate\';\n	$params[\'renderTV\']       = \'issueArticleDate\';\n	$params[\'orderBy\']        = \'pagetitle ASC\';\n	$params[\'debug\']          = \'0\';\n	$params[\'customLang\']     = \'polish-utf8\';\n\n	// prepare\n\n	$params[\'prepare\']        = \'dl_prepare_search\';\n\n	// content templates\n\n	$params[\'noneTPL\']        = \'@CODE: <div class=\"container-fluid\"><div class=\"content-canvas\"><div class=\"content-canvas__inner\"><p>Nic nie znaleziono :(</p></div></div></div>\';\n	$params[\'ownerTPL\']       = \'dl-search-wrap\';\n	$params[\'tpl\']            = \'dl-search-item\';\n	$params[\'noneWrapOuter\']  = \'0\';\n\n	// filters\n\n	$filters  = \'\';\n\n	// remove issues (containers)\n	//$filters .= \'content:c.template:!=:40;content:c.isfolder:!=:1\';\n	if ( !empty($_GET[\'title\']) )  $filters .= (empty($filters) ? \'\' : \';\') . \'content:c.pagetitle:%:\'.$modx->db->escape($_GET[\'title\']);\n	if ( !empty($_GET[\'author\']) ) $filters .= (empty($filters) ? \'\' : \';\') . \'tv:issueArticleAuthor:containsOne:\'.$modx->db->escape($_GET[\'author\']);\n	\n	// dateFrom\n	if ( !empty($_GET[\'dateFromYear\']) ) {\n		$dateFromYear  = $modx->db->escape($_GET[\'dateFromYear\']);\n		$dateFromMonth = $modx->db->escape($_GET[\'dateFromMonth\']);\n		$dateFromDay   = $modx->db->escape($_GET[\'dateFromDay\']);\n		if ( empty($_GET[\'dateFromMonth\']) ) {\n			$dateFromMonth = \'01\';\n		}\n		if ( empty($_GET[\'dateFromDay\']) ) {\n			$dateFromDay = \'01\';\n		}\n		$dateFrom = $dateFromYear.\'-\'.$dateFromMonth.\'-\'.$dateFromDay;\n		$filters .= (empty($filters) ? \'\' : \';\') . \'tvdate:issueArticleDate:>:\'.$dateFrom;\n	}\n	// dateTo\n	if ( !empty($_GET[\'dateToYear\']) ) {\n		$dateToYear  = $modx->db->escape($_GET[\'dateToYear\']);\n		$dateToMonth = $modx->db->escape($_GET[\'dateToMonth\']);\n		$dateToDay   = $modx->db->escape($_GET[\'dateToDay\']);\n		if ( empty($_GET[\'dateToMonth\']) ) {\n			$dateToMonth = \'01\';\n		}\n		if ( empty($_GET[\'dateToDay\']) ) {\n			$dateToDay = \'01\';\n		}\n		$dateTo = $dateToYear.\'-\'.$dateToMonth.\'-\'.$dateToDay;\n		$filters .= (empty($filters) ? \'\' : \';\') . \'tvdate:issueArticleDate:<:\'.$dateTo;\n	}\n	\n	$params[\'filters\'] = \'AND(\'.$filters.\')\';\n\n	// begin output\n\n	$output = \'\';\n\n	// generate list\n\n	$output .= $modx->runSnippet(\'DocLister\',$params);\n\n	return $output;\n}','0','{}',' ','0','1537711054','0');
