INSERT INTO `widok_evo_pagebuilder` VALUES ('405','386','default',NULL,'','presentation_image','{\"image\":\"assets/images/issues/2018/20/punkt-widokowy/dziedzictwo/10.jpg\",\"image_caption\":\"<p>Aleksander Lesser (1814-1884), <b><em>Władysław III warneńczyk</em></b> z cyklu <em>Kr&oacute;lowie polscy,</em><br><em>wizerunki</em>, Warszawa 1860. Na temat homoseksualizmu kr&oacute;la, w nim właśnie dopatrując się<br>przyczyny jego klęski i śmierci pod Warną, pisze Jan Długosz w Ksiądze XI/ Rocznik&oacute;w. Kr&oacute;l<br>bowiem, ,.skłonny do rozkoszy męskich\\\", ,.nie porzucił tych wstrętnych rozkoszy\\\" w trakcie<br>kampanii wojennej, narażając siebie i wojsko na gniew Boży.</p>\",\"fullscreen\":{},\"gif\":{},\"bgc\":\"white\"}','1','9');
INSERT INTO `widok_evo_pagebuilder` VALUES ('406','386','default',NULL,'','presentation_image','{\"image\":\"assets/images/issues/2018/20/punkt-widokowy/dziedzictwo/11.jpg\",\"image_caption\":\"<p>Aleksander Lesser (1814-1884), <b><em>Władysław III warneńczyk</em></b> z cyklu <em>Kr&oacute;lowie polscy,</em><br><em>wizerunki</em>, Warszawa 1860. Na temat homoseksualizmu kr&oacute;la, w nim właśnie dopatrując się<br>przyczyny jego klęski i śmierci pod Warną, pisze Jan Długosz w Ksiądze XI/ Rocznik&oacute;w. Kr&oacute;l<br>bowiem, \\\"skłonny do rozkoszy męskich\\\", ,.nie porzucił tych wstrętnych rozkoszy\\\" w trakcie<br>kampanii wojennej, narażając siebie i wojsko na gniew Boży.</p>\",\"fullscreen\":{\"0\":\"1\"},\"gif\":{},\"bgc\":\"black\"}','1','11');
