INSERT INTO `widok_evo_site_htmlsnippets` VALUES ('13','block-head','','2','none','39','0','[[siteConfig]]<!DOCTYPE html>\n<!--[if lt IE 7 ]><html dir=\"ltr\" lang=\"[+cfg_lang+]\" class=\"ie ie6 lte7 lte8 lte9\"><![endif]-->\n<!--[if IE 7 ]><html dir=\"ltr\" lang=\"[+cfg_lang+]\" class=\"ie ie7 lte8 lte9\"><![endif]-->\n<!--[if IE 8 ]><html dir=\"ltr\" lang=\"[+cfg_lang+]\" class=\"ie ie8 lte9\"><![endif]-->\n<!--[if IE 9 ]><html dir=\"ltr\" lang=\"[+cfg_lang+]\" class=\"ie ie9\"><![endif]-->\n<!--[if (gt IE 9)|!(IE)]><!--><html dir=\"ltr\" lang=\"[+cfg_lang+]\"> <!--<![endif]-->\n	<head>\n		<base href=\"[+cfg_url_base+]\">\n\n		<meta charset=\"utf-8\">\n		\n		<title>[+cfg_meta_title+]</title>\n\n		<meta name=\"description\" content=\"[*meta_description:notags:strip:esc*]\">\n		<meta name=\"subject\" content=\"company\">\n		<meta name=\"copyright\" content=\"[+lexicon.siteName+]\">\n		<meta name=\"author\" content=\"[+lexicon.siteName+]\">\n		<meta name=\"robots\" content=\"index,follow\">\n		<meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n\n		<link rel=\"canonical\" href=\"[+cfg_url_base+][[if? &is=`[*template*]:not:3` &then=`[~[*id*]~]`]]\">\n		<link rel=\"alternate\" href=\"[+cfg_url_base+]\" hreflang=\"pl\">\n		<link rel=\"alternate\" href=\"[+cfg_url_base+]en\" hreflang=\"en\">\n\n		<!-- Schema.org markup for Google+ -->\n		<meta itemprop=\"name\" content=\"[+cfg_meta_title_esc+]\">\n		<meta itemprop=\"description\" content=\"[*meta_description:notags:strip:esc*]\">\n		<meta itemprop=\"image\" content=\"[+cfg_meta_img+]\">\n\n		<!-- Twitter Card -->\n		<meta name=\"twitter:card\" value=\"summary\">\n		<meta name=\"twitter:title\" content=\"[+cfg_meta_title_esc+]\">\n		<meta name=\"twitter:description\" content=\"[*meta_description:notags:strip:esc*]\">\n		<meta name=\"twitter:image:src\" content=\"[+cfg_meta_img+]\">\n\n		<!-- Open Graph -->\n		<meta property=\"og:url\" content=\"[+cfg_url_base+][[if? &is=`[*template*]:not:3` &then=`[~[*id*]~]`]]\">\n		<meta property=\"og:site_name\" content=\"[+lexicon.siteName+]\">\n		<meta property=\"og:title\" content=\"[+cfg_meta_title_esc+]\">\n		<meta property=\"og:description\" content=\"[*meta_description:notags:strip:esc*]\">\n		<meta property=\"og:image\" content=\"[+cfg_meta_img+]\">\n		<meta property=\"og:image:type\" content=\"image/jpeg\">\n		<meta property=\"og:image:width\" content=\"1200\">\n		<meta property=\"og:image:height\" content=\"630\">\n		\n		<!-- Favicon -->\n		<link rel=\"apple-touch-icon\" sizes=\"180x180\" href=\"apple-touch-icon.png\">\n		<link rel=\"icon\" type=\"image/png\" sizes=\"32x32\" href=\"favicon-32x32.png\">\n		<link rel=\"icon\" type=\"image/png\" sizes=\"16x16\" href=\"favicon-16x16.png\">\n		<link rel=\"manifest\" href=\"site.webmanifest\">\n		<link rel=\"mask-icon\" href=\"safari-pinned-tab.svg\" color=\"#5bbad5\">\n		<meta name=\"apple-mobile-web-app-title\" content=\"Widok\">\n		<meta name=\"application-name\" content=\"Widok\">\n		<meta name=\"msapplication-TileColor\" content=\"#2b5797\">\n		<meta name=\"theme-color\" content=\"#ffffff\">\n		\n		[[if? &is=`[*template*]:is:3`           &then=`@TPL:block-issueColorCss-home`]]\n		[[if? &is=`[*template*]:in:17,40,43`    &then=`@TPL:block-issueColorCss-this`]]\n		[[if? &is=`[*template*]:!in:3,17,40,43` &then=`@TPL:block-issueColorCss-stored`]]\n\n	</head>\n	<body class=\"context-[+context+] tpl-[*template*] page-id-[*id*] parent-id-[*parent*]\">\n    \n    <div id=\"preloader\" aria-hidden=\"true\"></div>\n		<span id=\"js-selected-issue\" data-selected-issue-id=\"[+cfg_thisIssue_id+]\" data-selected-issue-color=\"[+cfg_thisIssue_color+]\" aria-hidden=\"true\"></span>\n\n		<header id=\"header\">\n     \n      <div class=\"c-navbar\">\n        <a class=\"c-sitelogo\" href=\"[+cfg_url_home+]\">\n          <span class=\"c-sitelogo__icon\"><img src=\"[+cfg_tpl_path+]img/logo.svg\" alt=\"[+lexicon.siteName+]\"></span>\n          <span class=\"c-sitelogo__full u-underline-border\">[+lexicon.siteName+]</span>\n        </a>\n        <div class=\"c-headertools\">\n          <ul>\n            <li class=\"c-headertools__lang\">\n							[[switchLangLink]]\n							<!-- [[if? &is=`[*translationSelector*]:!empty` \n							&then=`<a class=\"c-headertools__lang-link u-underline-border\" href=\"[~[*translationSelector*]~]\">[+cfg_switchLang_text+]</a>` \n							&else=`<a class=\"c-headertools__lang-link u-underline-border\" href=\"[+cfg_switchLang_link+]\">[+cfg_switchLang_text+]</a>`\n							]] -->\n            </li>\n            <li class=\"c-headertools__nav\">\n              <button type=\"button\" class=\"c-headertools__nav-button js-toggle-nav\">\n                <span></span>\n              </button>\n            </li>\n            <li class=\"c-headertools__search\">\n              <a href=\"[~[+cfg_id_search+]~]\">\n                <svg class=\"c-headertools__search-icon\" version=\"1.0\"\n                     xmlns=\"http://www.w3.org/2000/svg\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:a=\"http://ns.adobe.com/AdobeSVGViewerExtensions/3.0/\"\n                     x=\"0px\" y=\"0px\" width=\"28px\" height=\"28px\" viewBox=\"-0.399 -0.399 28 28\"\n                     overflow=\"visible\" enable-background=\"new -0.399 -0.399 28 28\" xml:space=\"preserve\">\n                  <defs>\n                  </defs>\n                  <g>\n                    <circle fill=\"none\" stroke=\"#000000\" stroke-width=\"2\" cx=\"12\" cy=\"12\" r=\"11\"/>\n                    <line fill=\"none\" stroke=\"#000000\" stroke-width=\"2\" x1=\"20.467\" y1=\"20.467\" x2=\"26.496\" y2=\"26.495\"/>\n                  </g>\n                </svg>\n              </a>\n            </li>\n          </ul>\n        </div>\n        <div class=\"c-headerissue\">\n          <span class=\"c-headerissue__title\">[+lexicon.numberShort+]</span>\n					[!headerCircleHtml!]\n        </div>\n      </div>\n      \n      <nav class=\"c-sitenav\">\n        <div class=\"c-sitenav__main\">\n          <ul>\n            <li class=\"c-sitenav__search\">\n              <svg version=\"1.0\"\n                   xmlns=\"http://www.w3.org/2000/svg\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" xmlns:a=\"http://ns.adobe.com/AdobeSVGViewerExtensions/3.0/\"\n                   x=\"0px\" y=\"0px\" width=\"28px\" height=\"28px\" viewBox=\"-0.399 -0.399 28 28\"\n                   overflow=\"visible\" enable-background=\"new -0.399 -0.399 28 28\" xml:space=\"preserve\">\n                <defs>\n                </defs>\n                <g>\n                  <circle fill=\"none\" stroke=\"#000000\" stroke-width=\"2\" cx=\"12\" cy=\"12\" r=\"11\"/>\n                  <line fill=\"none\" stroke=\"#000000\" stroke-width=\"2\" x1=\"20.467\" y1=\"20.467\" x2=\"26.496\" y2=\"26.495\"/>\n                </g>\n              </svg>\n              <a href=\"[~[+cfg_id_search+]~]\">Wyszukaj</a>\n            </li>\n            [[Wayfinder? \n						&startId=`[+cfg_root+]` \n						&level=`1` \n						&hereClass=`active` \n						&outerTpl=`wf-outer-empty` \n						&rowTpl=`wf-row-li` \n						&titleOfLinks=`pagetitle`\n						&textOfLinks=`pagetitle`\n						]]\n          </ul>\n        </div>\n      </nav>\n      \n    </header>\n    \n    <main id=\"main\">\n			','0','0','1552141830','0');
