INSERT INTO `widok_evo_site_htmlsnippets` VALUES ('136','dl-archive-issue','','2','none','48','0','<div class=\"c-issue-meta\">\n  <a href=\"[+url+]\">\n		<img class=\"c-issue-meta__img\" \n				 srcset=\"[+prepared.coverImage_3x+] 3x, \n								 [+prepared.coverImage_2x+] 2x, \n								 [+prepared.coverImage_1x+] 1x\" \n				 src=\"[+prepared.coverImage_1x+]\" \n				 alt=\"\">\n	</a>\n  <div class=\"c-issue-meta__info\">\n    <h3 class=\"c-issue-meta__title\">\n			<a href=\"[+url+]\">\n			<span class=\"combined\">\n				<span class=\"number\">[+lexicon.numberShort+] [+tv.issueNumber+][+prepared.issueNumberColon+]</span> \n				[+prepared.issueTitleHtml+]\n			</span>\n			</a>\n		</h3>\n    <div class=\"c-issue-meta__editors\"><p><span>[+prepared.variant+]: [+prepared.editors+]</span></p></div>\n    <div class=\"c-issue-meta__coverinfo\" data-lex-cover=\"[+lexicon.coverArtwork+]:\">[+tv.issueCoverInfoShort+]</div>\n  </div>\n  <div class=\"clearfix\" aria-hidden=\"true\"></div>\n</div>','0','1528469366','1548763327','0');
