INSERT INTO `widok_evo_site_htmlsnippets` VALUES ('154','block-issueColorCss-this','','2','none','39','0','<style>\n	\n  body.tpl-40.has-docked-header #header,\n  body.tpl-40 .c-headerissue__number,\n	.m-presentation--bgc-issue,\n	.m-presentation--big-text,\n	.m-presentation--slides .owl-dots .owl-dot.active span, \n	.m-presentation--slides .owl-dots .owl-dot:hover span, \n	.m-presentation--slides-alt .owl-dots .owl-dot.active span, \n	.m-presentation--slides-alt .owl-dots .owl-dot:hover span {\n    background-color: [+cfg_thisIssue_color+] !important;\n  }\n  body.tpl-40 .c-headerissue__number {\n    color: #000 !important;\n  }\n  body.tpl-40.has-docked-header .c-headerissue__number {\n    color: #fff !important;\n  }\n  body.tpl-40.has-docked-header .c-headerissue__number {\n		background-color: #000 !important;\n  }\n	\n  body.tpl-17 #header,\n  body.tpl-43 #header,\n	.u-typo ul li::before,\n	.c-jump2issue__circle {\n		background-color: [+cfg_thisIssue_color+] !important;\n	}\n	.c-headerissue__number {\n		color: #fff !important;\n		background-color: #000 !important;\n	}\n	.c-archive-year,\n	.c-article-meta__author,\n	.c-article-meta__author a,\n	.c-article-video__caption,\n	.c-article-video__caption *,\n	.c-issue-toc__section-title,\n	.c-issue-toc__article:before,\n	.c-issue-toc__article-authors,\n	.c-issue-toc__article--has-translation .c-issue-toc__article-authors a,\n	.u-typo figcaption,\n	.u-typo figcaption *,\n	.u-typo a,\n	.u-typo a:hover,\n	.u-typo h3,\n	.u-typo h4,\n	.c-footnotes__item,\n	.c-sitenav__search,\n	.m-presentation--image-text .u-typo,\n	.m-presentation--full-image figcaption,\n  .sl-wrapper .sl-close,\n  .sl-wrapper .sl-image .sl-caption,\n  .sl-wrapper .sl-navigation button,\n	.m-article .u-typo h2,\n	.m-bibliography__content,\n	.m-footnotes p,\n	.m-presentation__caption,\n	.m-presentation__caption * {\n		color: [+cfg_thisIssue_color+] !important;\n	}\n	\n	.c-archive-year span {\n		border-color: [+cfg_thisIssue_color+] !important;\n	}\n	\n	.m-presentation--bgc-issue.m-presentation--image-text,\n	.m-presentation--bgc-issue.m-presentation--image-text .u-typo,\n	.m-presentation--bgc-issue .m-presentation__caption,\n	.m-presentation--bgc-issue .m-presentation__caption *,\n	.m-presentation--bgc-issue .c-article-meta__author,\n	.m-presentation--bgc-issue .c-article-meta__author a {\n		color: #000 !important;\n	}\n	\n	.c-sitenav__search svg circle, \n	.c-sitenav__search svg line {\n		stroke: [+cfg_thisIssue_color+] !important;\n	}\n	\n	.m-presentation__control--mute .speaker-icon path {\n    fill: [+cfg_thisIssue_color+] !important;\n	}\n	.m-presentation__control--mute.is-inverse .speaker-icon path {\n    fill: #000 !important;\n	}\n	\n</style>','0','0','1551541338','0');
INSERT INTO `widok_evo_site_htmlsnippets` VALUES ('155','block-issueColorCss-latest','','2','none','39','0','<style>\n	#header,\n	.form-control,\n	.u-typo ul li::before {\n		background-color: [+cfg_latestIssue_color+] !important;\n	}\n	\n	.form-control {\n		border-color: [+cfg_latestIssue_color+] !important;\n	}\n	\n	.c-headerissue__number {\n		color: #fff !important;\n		background-color: #000 !important;\n	}\n	\n	.btn-primary,\n	.c-issue-toc__section-title,\n	.c-issue-toc__article:before,\n	.c-issue-toc__article-authors,\n	.c-issue-toc__article--has-translation .c-issue-toc__article-authors a,\n	.c-article-meta__author,\n	.c-article-meta__author a,\n	.u-typo figcaption,\n	.u-typo a,\n	.u-typo a:hover,\n	.u-typo h1,\n	.u-typo h2,\n	.u-typo h3,\n	.u-typo h4,\n	.c-footnotes__item,\n	.sl-wrapper .sl-close,\n	.sl-wrapper .sl-image .sl-caption,\n	.sl-wrapper .sl-navigation button,\n	.c-subnav li.active span, \n	.c-subnav a:hover span,\n	.c-team__meta a {\n		color: [+cfg_latestIssue_color+] !important;\n	}\n	\n	.c-subnav li.active span, \n	.c-subnav a:hover span {\n		background-image: -moz-linear-gradient(left, [+cfg_latestIssue_color+] 50%, [+cfg_latestIssue_color+] 50%) !important;\n		background-image: -webkit-linear-gradient(left, [+cfg_latestIssue_color+] 50%, [+cfg_latestIssue_color+] 50%) !important;\n    background-image: linear-gradient(to right, [+cfg_latestIssue_color+] 50%, [+cfg_latestIssue_color+] 50%) !important;\n	}\n</style>','0','0','1542543013','1');
