INSERT INTO `widok_evo_site_htmlsnippets` VALUES ('168','pb-tpl-presentation-image','PageBuilder block tpl','2','none','47','0','[[if? \n&is=`[+fullscreen+]:!empty` \n&then=`\n<section class=\"fullpage-section fullpage-section--bgc-black\" data-anchor=\"[+iteration+]\">\n	<div class=\"m-presentation m-presentation--full-image m-presentation--bgc-black\">\n		<figure>` \n&else=`\n			<section class=\"fullpage-section fullpage-section--bgc-[+bgc+]\" data-anchor=\"[+iteration+]\">\n				<div class=\"m-presentation m-presentation--image m-presentation--bgc-[+bgc+]\">\n					<div class=\"m-presentation__outer\">\n						<div class=\"m-presentation__inner\">\n							<div class=\"container-fluid u-typo\">\n								<figure>`\n]]\n\n[+prepared.image_html+]\n[+prepared.figcaption_html+]\n	\n[[if? \n&is=`[+fullscreen+]:!empty` \n&then=`\n								</figure>\n							</div>\n							</section>` \n&else=`\n						</figure>\n					</div>\n				</div>\n			</div>\n	</div>\n</section>`\n]]','0','0','1548854289','0');
INSERT INTO `widok_evo_site_htmlsnippets` VALUES ('177','pb-tpl-content-video Kopia','PageBuilder block tpl','2','none','47','0','<section class=\"m-page-content m-page-content--video\">\n	<div class=\"embed-responsive embed-responsive-16by9\">\n\n		[[if? &separator=`||` &is=`[+service+]||is||youtube` &then=`\n		<iframe class=\"embed-responsive-item\" src=\"https://www.youtube.com/embed/[[getYouTubeId?&url=`[+url+]`]]?rel=0\" webkitallowfullscreen mozallowfullscreen allowfullscreen></iframe>\n		`]]\n\n		[[if? &separator=`||` &is=`[+service+]||is||vimeo` &then=`\n		<iframe class=\"embed-responsive-item\" src=\"https://player.vimeo.com/video/[[getVimeoId?&url=`[+url+]`]]?title=0&byline=0&portrait=0\" webkitallowfullscreen mozallowfullscreen allowfullscreen></iframe>\n		`]]\n\n	</div>\n</section>','0','0','1548068286','1');
