INSERT INTO `widok_evo_site_htmlsnippets` VALUES ('174','pb-tpl-profiles__item Kopia','PageBuilder block tpl','2','none','47','0','<div class=\"c-team\">\n	[[if? &is=`[+image+]:notempty` &then=`\n	<div class=\"c-team__image\">\n		<img src=\"[+image+]\" alt=\"\">\n	</div>\n	`]]\n	<div class=\"c-team__desc\">\n		<h3 class=\"c-team__title\">[+name+]</h3>\n		<p class=\"c-team__meta\">\n			[[if? &separator=`||` &is=`[+university+]||notempty` &then=`<span><a href=\"[[addHttp?&url=`[+url+]`]]\" target=\"_blank\">[+url+]</a></span>`]]\n			[[if? &separator=`||` &is=`[+url+]||notempty` &then=`<span>[+university+]</span>`]]\n		</p>\n		<div class=\"u-typo\">\n			[+content+]\n		</div>\n	</div>\n</div>','0','0','1547486180','1');
