INSERT INTO `widok_evo_site_htmlsnippets` VALUES ('179','pb-tpl-presentation-audio','PageBuilder block tpl','2','none','47','0','<section class=\"fullpage-section fullpage-section--audio fullpage-section--bgc-[+bgc+]\" data-anchor=\"[+iteration+]\">\n	<div class=\"m-presentation m-presentation--audio m-presentation--bgc-[+bgc+]\">\n		<div class=\"m-presentation__outer\">\n			<div class=\"m-presentation__inner\">\n				<div class=\"container-fluid\">\n\n					<div id=\"audioplayer\" class=\"audioplayer audioplayer--jplayer has-metronome\">\n\n						<div id=\"jplayer_[+audio_player_id+]\" class=\"jp-jplayer\" data-title=\"\" data-file=\"[+audio_file+]\"></div>\n						<div id=\"jp_container_[+audio_player_id+]\" class=\"jp-audio\" role=\"application\" aria-label=\"media player\">\n							<div class=\"jp-type-single\">\n								<div class=\"jp-gui jp-interface\">\n\n									<!-- controls -->\n									<div class=\"jp-controls\">\n										<button class=\"audioplayer-control audioplayer-control-play jp-play active\" title=\"Play/Pause\" role=\"button\" tabindex=\"0\"></button>\n									</div>\n\n									<!-- time, seek bar -->\n									<div class=\"audioplayer-time-container\">\n										<div class=\"audioplayer-song-slider audioplayer-song-slider--jplayer\">\n											<div class=\"jp-progress\">\n												<div class=\"jp-seek-bar\">\n													<div class=\"jp-play-bar\"></div>\n												</div>\n											</div>\n										</div>\n										<span class=\"audioplayer-current-time jp-current-time\" role=\"timer\" aria-label=\"time\">&nbsp;</span>\n									</div>\n								</div>\n\n								<div class=\"jp-no-solution\">\n									<span>Update Required</span>\n									To play the media you will need to either update your browser to a recent version or update your <a href=\"http://get.adobe.com/flashplayer/\" target=\"_blank\">Flash plugin</a>.\n								</div>\n\n							</div>\n						</div>\n						<div style=\"clear:both;\"></div>\n					</div>\n\n				</div>\n			</div>\n		</div>\n		[+prepared.caption_html+]\n	</div>\n</section>','0','0','1548852998','0');
