INSERT INTO `widok_evo_site_plugins` VALUES ('113','ManagerCssTree','Customize Evo Manager look','0','4','0','$e = & $modx->Event;\n\nif ( $e->name == \"OnManagerTreePrerender\" ) {\n	$html = \'\n	<style>\n		#treeMenu_addresource,\n		#treeMenu_addweblink,\n		#treeMenu_sortingindex {\n			display: none !important;\n		}\n		#treeRoot > div:nth-child(-n+\'.$contextHowMany.\') > .node {\n			padding-top:    5px;\n			padding-bottom: 5px;\n			background-color: rgba(0,0,0,0.03) !important;\n		}\n		#treeRoot > div:nth-child(-n+\'.$contextHowMany.\') > div {\n			padding-top:    5px;\n			padding-bottom: 5px;\n		}\n		.dark #treeRoot > div:nth-child(-n+\'.$contextHowMany.\') > .node,\n		.darkness #treeRoot > div:nth-child(-n+\'.$contextHowMany.\') > .node {\n			background-color: rgba(255,255,255,0.05) !important;\n		}\n		#treeRoot .tree-node-addon > a {\n			opacity: 0.2;\n			float:right;\n			margin: .2rem .25rem -1.5rem;\n			padding: 0 !important;\n			cursor: pointer;\n		}\n		#treeRoot .tree-node-addon > a:hover {\n			opacity: 1;\n		}\n		#treeRoot .tree-node-addon:hover,\n		#treeRoot .tree-node-addon > a:hover {\n			background-color: transparent !important;\n		}\n		#treeRoot > .tree-node-addon > a {\n			margin-top: .4rem;\n		}\n	</style>\n	\';\n	$e->output($html);\n}','1','{\n  \"contextHowMany\": [\n    {\n      \"label\": \"Number of contexts\",\n      \"type\": \"text\",\n      \"value\": \"10\",\n      \"default\": \"\",\n      \"desc\": \"\"\n    }\n  ]\n}','0',' ','1528753210','1551536152');
