INSERT INTO `widok_evo_site_plugins` VALUES ('102','TinyMCE4','<strong>4.7.4</strong> Javascript rich text editor','0','4','0','/**\n * TinyMCE4\n *\n * Javascript rich text editor\n *\n * @category    plugin\n * @version     4.7.4\n * @license     http://www.gnu.org/copyleft/gpl.html GNU Public License (GPL)\n * @internal    @properties &styleFormats=Custom Style Formats <b>RAW</b><br/><br/><ul><li>leave empty to use below block/inline formats</li><li>allows simple-format: <i>Title,cssClass|Title2,cssClass2</i></li><li>Also accepts full JSON-config as per TinyMCE4 docs / configure / content-formating / style_formats</li></ul>;textarea; &styleFormats_inline=Custom Style Formats <b>INLINE</b><br/><br/><ul><li>will wrap selected text with span-tag + css-class</li><li>simple-format only</li></ul>;textarea;InlineTitle,cssClass1|InlineTitle2,cssClass2 &styleFormats_block=Custom Style Formats <b>BLOCK</b><br/><br/><ul><li>will add css-class to selected block-element</li><li>simple-format only</li></ul>;textarea;BlockTitle,cssClass3|BlockTitle2,cssClass4 &customParams=Custom Parameters<br/><b>(Be careful or leave empty!)</b>;textarea; &entityEncoding=Entity Encoding;list;named,numeric,raw;named &entities=Entities;text; &pathOptions=Path Options;list;Site config,Absolute path,Root relative,URL,No convert;Site config &resizing=Advanced Resizing;list;true,false;false &disabledButtons=Disabled Buttons;text; &webTheme=Web Theme;test;webuser &webPlugins=Web Plugins;text; &webButtons1=Web Buttons 1;text;bold italic underline strikethrough removeformat alignleft aligncenter alignright &webButtons2=Web Buttons 2;text;link unlink image undo redo &webButtons3=Web Buttons 3;text; &webButtons4=Web Buttons 4;text; &webAlign=Web Toolbar Alignment;list;ltr,rtl;ltr &width=Width;text;100% &height=Height;text;400px &introtextRte=<b>Introtext RTE</b><br/>add richtext-features to \"introtext\";list;enabled,disabled;disabled &inlineMode=<b>Inline-Mode</b>;list;enabled,disabled;disabled &inlineTheme=<b>Inline-Mode</b><br/>Theme;text;inline &browser_spellcheck=<b>Browser Spellcheck</b><br/>At least one dictionary must be installed inside your browser;list;enabled,disabled;disabled &paste_as_text=<b>Force Paste as Text</b>;list;enabled,disabled;disabled\n * @internal    @events OnLoadWebDocument,OnParseDocument,OnWebPagePrerender,OnLoadWebPageCache,OnRichTextEditorRegister,OnRichTextEditorInit,OnInterfaceSettingsRender\n * @internal    @modx_category Manager and Admin\n * @internal    @legacy_names TinyMCE Rich Text Editor\n * @internal    @installset base\n * @logo        /assets/plugins/tinymce4/tinymce/logo.png\n * @reportissues https://github.com/extras-evolution/tinymce4-for-modx-evo\n * @documentation Plugin docs https://github.com/extras-evolution/tinymce4-for-modx-evo\n * @documentation Official TinyMCE4-docs https://www.tinymce.com/docs/\n * @author      Deesen\n * @lastupdate  2018-01-17\n */\nif (!defined(\'MODX_BASE_PATH\')) { die(\'What are you doing? Get out of here!\'); }\n\nrequire(MODX_BASE_PATH.\"assets/plugins/tinymce4/plugin.tinymce.inc.php\");','0','{\n  \"styleFormats\": [\n    {\n      \"label\": \"Custom Style Formats <b>RAW</b><br/><br/><ul><li>leave empty to use below block/inline formats</li><li>allows simple-format: <i>Title,cssClass|Title2,cssClass2</i></li><li>Also accepts full JSON-config as per TinyMCE4 docs / configure / content-formating / style_formats</li></ul>\",\n      \"type\": \"textarea\",\n      \"value\": \"\",\n      \"default\": \"\",\n      \"desc\": \"\"\n    }\n  ],\n  \"styleFormats_inline\": [\n    {\n      \"label\": \"Custom Style Formats <b>INLINE</b><br/><br/><ul><li>will wrap selected text with span-tag + css-class</li><li>simple-format only</li></ul>\",\n      \"type\": \"textarea\",\n      \"value\": \"InlineTitle,cssClass1|InlineTitle2,cssClass2\",\n      \"default\": \"InlineTitle,cssClass1|InlineTitle2,cssClass2\",\n      \"desc\": \"\"\n    }\n  ],\n  \"styleFormats_block\": [\n    {\n      \"label\": \"Custom Style Formats <b>BLOCK</b><br/><br/><ul><li>will add css-class to selected block-element</li><li>simple-format only</li></ul>\",\n      \"type\": \"textarea\",\n      \"value\": \"BlockTitle,cssClass3|BlockTitle2,cssClass4\",\n      \"default\": \"BlockTitle,cssClass3|BlockTitle2,cssClass4\",\n      \"desc\": \"\"\n    }\n  ],\n  \"customParams\": [\n    {\n      \"label\": \"Custom Parameters<br/><b>(Be careful or leave empty!)</b>\",\n      \"type\": \"textarea\",\n      \"value\": \"valid_elements : \'h1,h2,h3,h4,h5,p,br,blockquote,footer,a[href|name|id|target],b/strong,em/i,u,ul,ol,li,dl,dt,dd,sup,sub,table,thead,tfoot,tr,th,td,img\',\\nvalid_styles: {\\n  \'*\': \'\'\\n}\",\n      \"default\": \"\",\n      \"desc\": \"\"\n    }\n  ],\n  \"entityEncoding\": [\n    {\n      \"label\": \"Entity Encoding\",\n      \"type\": \"list\",\n      \"value\": \"named\",\n      \"options\": \"named,numeric,raw\",\n      \"default\": \"named\",\n      \"desc\": \"\"\n    }\n  ],\n  \"entities\": [\n    {\n      \"label\": \"Entities\",\n      \"type\": \"text\",\n      \"value\": \"\",\n      \"default\": \"\",\n      \"desc\": \"\"\n    }\n  ],\n  \"pathOptions\": [\n    {\n      \"label\": \"Path Options\",\n      \"type\": \"list\",\n      \"value\": \"Site config\",\n      \"options\": \"Site config,Absolute path,Root relative,URL,No convert\",\n      \"default\": \"Site config\",\n      \"desc\": \"\"\n    }\n  ],\n  \"resizing\": [\n    {\n      \"label\": \"Advanced Resizing\",\n      \"type\": \"list\",\n      \"value\": \"false\",\n      \"options\": \"true,false\",\n      \"default\": \"false\",\n      \"desc\": \"\"\n    }\n  ],\n  \"disabledButtons\": [\n    {\n      \"label\": \"Disabled Buttons\",\n      \"type\": \"text\",\n      \"value\": \"\",\n      \"default\": \"\",\n      \"desc\": \"\"\n    }\n  ],\n  \"webTheme\": [\n    {\n      \"label\": \"Web Theme\",\n      \"type\": \"test\",\n      \"value\": \"webuser\",\n      \"default\": \"webuser\",\n      \"desc\": \"\"\n    }\n  ],\n  \"webPlugins\": [\n    {\n      \"label\": \"Web Plugins\",\n      \"type\": \"text\",\n      \"value\": \"\",\n      \"default\": \"\",\n      \"desc\": \"\"\n    }\n  ],\n  \"webButtons1\": [\n    {\n      \"label\": \"Web Buttons 1\",\n      \"type\": \"text\",\n      \"value\": \"bold italic underline strikethrough removeformat alignleft aligncenter alignright\",\n      \"default\": \"bold italic underline strikethrough removeformat alignleft aligncenter alignright\",\n      \"desc\": \"\"\n    }\n  ],\n  \"webButtons2\": [\n    {\n      \"label\": \"Web Buttons 2\",\n      \"type\": \"text\",\n      \"value\": \"link unlink image undo redo\",\n      \"default\": \"link unlink image undo redo\",\n      \"desc\": \"\"\n    }\n  ],\n  \"webButtons3\": [\n    {\n      \"label\": \"Web Buttons 3\",\n      \"type\": \"text\",\n      \"value\": \"\",\n      \"default\": \"\",\n      \"desc\": \"\"\n    }\n  ],\n  \"webButtons4\": [\n    {\n      \"label\": \"Web Buttons 4\",\n      \"type\": \"text\",\n      \"value\": \"\",\n      \"default\": \"\",\n      \"desc\": \"\"\n    }\n  ],\n  \"webAlign\": [\n    {\n      \"label\": \"Web Toolbar Alignment\",\n      \"type\": \"list\",\n      \"value\": \"ltr\",\n      \"options\": \"ltr,rtl\",\n      \"default\": \"ltr\",\n      \"desc\": \"\"\n    }\n  ],\n  \"width\": [\n    {\n      \"label\": \"Width\",\n      \"type\": \"text\",\n      \"value\": \"100%\",\n      \"default\": \"100%\",\n      \"desc\": \"\"\n    }\n  ],\n  \"height\": [\n    {\n      \"label\": \"Height\",\n      \"type\": \"text\",\n      \"value\": \"400px\",\n      \"default\": \"400px\",\n      \"desc\": \"\"\n    }\n  ],\n  \"introtextRte\": [\n    {\n      \"label\": \"<b>Introtext RTE</b><br/>add richtext-features to \\\"introtext\\\"\",\n      \"type\": \"list\",\n      \"value\": \"disabled\",\n      \"options\": \"enabled,disabled\",\n      \"default\": \"disabled\",\n      \"desc\": \"\"\n    }\n  ],\n  \"inlineMode\": [\n    {\n      \"label\": \"<b>Inline-Mode</b>\",\n      \"type\": \"list\",\n      \"value\": \"disabled\",\n      \"options\": \"enabled,disabled\",\n      \"default\": \"disabled\",\n      \"desc\": \"\"\n    }\n  ],\n  \"inlineTheme\": [\n    {\n      \"label\": \"<b>Inline-Mode</b><br/>Theme\",\n      \"type\": \"text\",\n      \"value\": \"inline\",\n      \"default\": \"inline\",\n      \"desc\": \"\"\n    }\n  ],\n  \"browser_spellcheck\": [\n    {\n      \"label\": \"<b>Browser Spellcheck</b><br/>At least one dictionary must be installed inside your browser\",\n      \"type\": \"list\",\n      \"value\": \"disabled\",\n      \"options\": \"enabled,disabled\",\n      \"default\": \"disabled\",\n      \"desc\": \"\"\n    }\n  ],\n  \"paste_as_text\": [\n    {\n      \"label\": \"<b>Force Paste as Text</b>\",\n      \"type\": \"list\",\n      \"value\": \"disabled\",\n      \"options\": \"enabled,disabled\",\n      \"default\": \"disabled\",\n      \"desc\": \"\"\n    }\n  ]\n}','0',' ','0','1551538730');
INSERT INTO `widok_evo_site_plugins` VALUES ('106','CodeMirror','<strong>1.5</strong> JavaScript library that can be used to create a relatively pleasant editor interface based on CodeMirror 5.33 (released on 21-12-2017)','0','4','0','\n/**\n * CodeMirror\n *\n * JavaScript library that can be used to create a relatively pleasant editor interface based on CodeMirror 5.33 (released on 21-12-2017)\n *\n * @category    plugin\n * @version     1.5\n * @license     http://www.gnu.org/copyleft/gpl.html GNU Public License (GPL)\n * @package     evo\n * @internal    @events OnDocFormRender,OnChunkFormRender,OnModFormRender,OnPluginFormRender,OnSnipFormRender,OnTempFormRender,OnRichTextEditorInit\n * @internal    @modx_category Manager and Admin\n * @internal    @properties &theme=Theme;list;default,ambiance,blackboard,cobalt,eclipse,elegant,erlang-dark,lesser-dark,midnight,monokai,neat,night,one-dark,rubyblue,solarized,twilight,vibrant-ink,xq-dark,xq-light;default &darktheme=Dark Theme;list;default,ambiance,blackboard,cobalt,eclipse,elegant,erlang-dark,lesser-dark,midnight,monokai,neat,night,one-dark,rubyblue,solarized,twilight,vibrant-ink,xq-dark,xq-light;one-dark &fontSize=Font-size;list;10,11,12,13,14,15,16,17,18;14 &lineHeight=Line-height;list;1,1.1,1.2,1.3,1.4,1.5;1.3 &indentUnit=Indent unit;int;4 &tabSize=The width of a tab character;int;4 &lineWrapping=lineWrapping;list;true,false;true &matchBrackets=matchBrackets;list;true,false;true &activeLine=activeLine;list;true,false;false &emmet=emmet;list;true,false;true &search=search;list;true,false;false &indentWithTabs=indentWithTabs;list;true,false;true &undoDepth=undoDepth;int;200 &historyEventDelay=historyEventDelay;int;1250\n * @internal    @installset base\n * @reportissues https://github.com/evolution-cms/evolution/issues/\n * @documentation Official docs https://codemirror.net/doc/manual.html\n * @author      hansek from http://www.modxcms.cz\n * @author      update Mihanik71\n * @author      update Deesen\n * @author      update 64j\n * @lastupdate  08-01-2018\n */\n\n$_CM_BASE = \'assets/plugins/codemirror/\';\n\n$_CM_URL = $modx->config[\'site_url\'] . $_CM_BASE;\n\nrequire(MODX_BASE_PATH. $_CM_BASE .\'codemirror.plugin.php\');','0','{\"theme\":[{\"label\":\"Theme\",\"type\":\"list\",\"value\":\"default\",\"options\":\"default,ambiance,blackboard,cobalt,eclipse,elegant,erlang-dark,lesser-dark,midnight,monokai,neat,night,one-dark,rubyblue,solarized,twilight,vibrant-ink,xq-dark,xq-light\",\"default\":\"default\",\"desc\":\"\"}],\"darktheme\":[{\"label\":\"Dark Theme\",\"type\":\"list\",\"value\":\"default\",\"options\":\"default,ambiance,blackboard,cobalt,eclipse,elegant,erlang-dark,lesser-dark,midnight,monokai,neat,night,one-dark,rubyblue,solarized,twilight,vibrant-ink,xq-dark,xq-light\",\"default\":\"one-dark\",\"desc\":\"\"}],\"fontSize\":[{\"label\":\"Font-size\",\"type\":\"list\",\"value\":\"12\",\"options\":\"10,11,12,13,14,15,16,17,18\",\"default\":\"14\",\"desc\":\"\"}],\"lineHeight\":[{\"label\":\"Line-height\",\"type\":\"list\",\"value\":\"1.3\",\"options\":\"1,1.1,1.2,1.3,1.4,1.5\",\"default\":\"1.3\",\"desc\":\"\"}],\"indentUnit\":[{\"label\":\"Indent unit\",\"type\":\"int\",\"value\":\"2\",\"default\":\"4\",\"desc\":\"\"}],\"tabSize\":[{\"label\":\"The width of a tab character\",\"type\":\"int\",\"value\":\"2\",\"default\":\"4\",\"desc\":\"\"}],\"lineWrapping\":[{\"label\":\"lineWrapping\",\"type\":\"list\",\"value\":\"true\",\"options\":\"true,false\",\"default\":\"true\",\"desc\":\"\"}],\"matchBrackets\":[{\"label\":\"matchBrackets\",\"type\":\"list\",\"value\":\"true\",\"options\":\"true,false\",\"default\":\"true\",\"desc\":\"\"}],\"activeLine\":[{\"label\":\"activeLine\",\"type\":\"list\",\"value\":\"false\",\"options\":\"true,false\",\"default\":\"false\",\"desc\":\"\"}],\"emmet\":[{\"label\":\"emmet\",\"type\":\"list\",\"value\":\"true\",\"options\":\"true,false\",\"default\":\"true\",\"desc\":\"\"}],\"search\":[{\"label\":\"search\",\"type\":\"list\",\"value\":\"false\",\"options\":\"true,false\",\"default\":\"false\",\"desc\":\"\"}],\"indentWithTabs\":[{\"label\":\"indentWithTabs\",\"type\":\"list\",\"value\":\"true\",\"options\":\"true,false\",\"default\":\"true\",\"desc\":\"\"}],\"undoDepth\":[{\"label\":\"undoDepth\",\"type\":\"int\",\"value\":\"200\",\"default\":\"200\",\"desc\":\"\"}],\"historyEventDelay\":[{\"label\":\"historyEventDelay\",\"type\":\"int\",\"value\":\"1250\",\"default\":\"1250\",\"desc\":\"\"}]}','0',' ','0','1525780820');
INSERT INTO `widok_evo_site_plugins` VALUES ('82','sgThumb','<b>1.2.2</b> Plugin to create thumbnails for SimpleGallery images','0','14','0','/**\n * sgThumb\n * \n * Plugin to create thumbnails for SimpleGallery images\n *\n * @category 	plugin\n * @version 	1.2.2\n * @license 	http://www.gnu.org/copyleft/gpl.html GNU Public License (GPL)\n * @author      Pathologic (m@xim.name)\n * @internal	@properties &tconfig=Thumbnails Configuration;textarea;See plugin code for details &keepOriginal=Keep original images;list;No,Yes;No &originalFolder=Folder for original images;text;original\n * @internal	@events OnFileBrowserUpload,OnSimpleGalleryRefresh,OnSimpleGalleryDelete,OnSimpleGalleryMove\n * @internal    @installset base\n */\n\nrequire MODX_BASE_PATH.\'assets/plugins/simplegallery/plugin.sgthumb.php\';','0','{\n  \"tconfig\": [\n    {\n      \"label\": \"Thumbnails Configuration\",\n      \"type\": \"textarea\",\n      \"value\": \"[\\n{\\\"template\\\":21,\\\"options\\\":\\\"w=250&h=250&zc=1&f=jpg&q=80\\\",\\\"folder\\\":\\\"thumb\\\"},\\n{\\\"template\\\":21,\\\"options\\\":\\\"w=1600&h=1600&f=jpg&q=80\\\",\\\"folder\\\":\\\"zoom\\\"}\\n]\",\n      \"default\": \"See plugin code for details\",\n      \"desc\": \"\"\n    }\n  ],\n  \"keepOriginal\": [\n    {\n      \"label\": \"Keep original images\",\n      \"type\": \"list\",\n      \"value\": \"No\",\n      \"options\": \"No,Yes\",\n      \"default\": \"No\",\n      \"desc\": \"\"\n    }\n  ],\n  \"originalFolder\": [\n    {\n      \"label\": \"Folder for original images\",\n      \"type\": \"text\",\n      \"value\": \"original\",\n      \"default\": \"original\",\n      \"desc\": \"\"\n    }\n  ]\n}','1',' ','1503945941','1551123344');
INSERT INTO `widok_evo_site_plugins` VALUES ('83','SimpleGallery','<b>1.2.2</b> Plugin to create image galleries','0','14','0','/**\n * SimpleGallery\n * \n * Plugin to create image galleries\n *\n * @category 	plugin\n * @version 	1.2.2\n * @license 	http://www.gnu.org/copyleft/gpl.html GNU Public License (GPL)\n * @author      Pathologic (m@xim.name)\n * @internal	@properties &tabName=Tab name;text;SimpleGallery &controller=Controller class;text; &templates=Templates;text; &documents=Documents;text; &ignoreDoc=Ignore Documents;text; &role=Roles;text; &folder=Galleries folder;text;assets/galleries/ &thumbsCache=Thumbs cache folder;text;assets/.sgThumbs/ &w=Thumbs width;text;140 &h=Thumbs height;text;105 &customThumbOptions=Custom thumb options;text; &clientResize=Client Resize;list;No,Yes;No &jpegQuality=Jpeg quality;text;0.93 &skipPHPThumb=Skip PHPThumb;list;No,Yes;No \n * @internal	@events OnDocFormRender,OnEmptyTrash\n * @internal    @installset base\n */\n\nrequire MODX_BASE_PATH.\'assets/plugins/simplegallery/plugin.simplegallery.php\';\n','0','{\n  \"tabName\": [\n    {\n      \"label\": \"Tab name\",\n      \"type\": \"text\",\n      \"value\": \"Gallery\",\n      \"default\": \"SimpleGallery\",\n      \"desc\": \"\"\n    }\n  ],\n  \"controller\": [\n    {\n      \"label\": \"Controller class\",\n      \"type\": \"text\",\n      \"value\": \"\",\n      \"default\": \"\",\n      \"desc\": \"\"\n    }\n  ],\n  \"templates\": [\n    {\n      \"label\": \"Templates\",\n      \"type\": \"text\",\n      \"value\": \"21\",\n      \"default\": \"\",\n      \"desc\": \"\"\n    }\n  ],\n  \"documents\": [\n    {\n      \"label\": \"Documents\",\n      \"type\": \"text\",\n      \"value\": \"\",\n      \"default\": \"\",\n      \"desc\": \"\"\n    }\n  ],\n  \"ignoreDoc\": [\n    {\n      \"label\": \"Ignore Documents\",\n      \"type\": \"text\",\n      \"value\": \"\",\n      \"default\": \"\",\n      \"desc\": \"\"\n    }\n  ],\n  \"role\": [\n    {\n      \"label\": \"Roles\",\n      \"type\": \"text\",\n      \"value\": \"\",\n      \"default\": \"\",\n      \"desc\": \"\"\n    }\n  ],\n  \"folder\": [\n    {\n      \"label\": \"Galleries folder\",\n      \"type\": \"text\",\n      \"value\": \"assets/galleries/sg/\",\n      \"default\": \"assets/galleries/\",\n      \"desc\": \"\"\n    }\n  ],\n  \"thumbsCache\": [\n    {\n      \"label\": \"Thumbs cache folder\",\n      \"type\": \"text\",\n      \"value\": \"assets/galleries/sg/_thumbs/\",\n      \"default\": \"assets/.sgThumbs/\",\n      \"desc\": \"\"\n    }\n  ],\n  \"w\": [\n    {\n      \"label\": \"Thumbs width\",\n      \"type\": \"text\",\n      \"value\": \"150\",\n      \"default\": \"140\",\n      \"desc\": \"\"\n    }\n  ],\n  \"h\": [\n    {\n      \"label\": \"Thumbs height\",\n      \"type\": \"text\",\n      \"value\": \"150\",\n      \"default\": \"105\",\n      \"desc\": \"\"\n    }\n  ],\n  \"customThumbOptions\": [\n    {\n      \"label\": \"Custom thumb options\",\n      \"type\": \"text\",\n      \"value\": \"wl=[+w+]&hp=[+h+]&zc=1&f=jpg\",\n      \"default\": \"\",\n      \"desc\": \"\"\n    }\n  ],\n  \"clientResize\": [\n    {\n      \"label\": \"Client Resize\",\n      \"type\": \"list\",\n      \"value\": \"No\",\n      \"options\": \"No,Yes\",\n      \"default\": \"No\",\n      \"desc\": \"\"\n    }\n  ],\n  \"jpegQuality\": [\n    {\n      \"label\": \"Jpeg quality\",\n      \"type\": \"text\",\n      \"value\": \"0.80\",\n      \"default\": \"0.93\",\n      \"desc\": \"\"\n    }\n  ],\n  \"skipPHPThumb\": [\n    {\n      \"label\": \"Skip PHPThumb\",\n      \"type\": \"list\",\n      \"value\": \"Yes\",\n      \"options\": \"No,Yes\",\n      \"default\": \"No\",\n      \"desc\": \"\"\n    }\n  ]\n}','1',' ','1503945964','1551123338');
INSERT INTO `widok_evo_site_plugins` VALUES ('78','PageBuilder','<b>1.3.0</b> Creates form for manage content sections','0','4','0','/**\n * PageBuilder\n * \n * Creates form for manage content sections\n *\n * @category    plugin\n * @version     1.3.0\n * @author      mnoskov\n * @internal    @properties &tabName=Tab name;text;Page Builder &addType=Add type;menu;dropdown,icons,images;dropdown &placement=Placement;menu;content,tab;tab\n * @internal    @events OnWebPageInit,OnManagerPageInit,OnDocFormRender,OnDocFormSave,OnBeforeEmptyTrash,OnDocDuplicate \n * @internal    @modx_category Manager and Admin\n * @internal    @installset base,sample\n */\n\ninclude_once MODX_BASE_PATH . \'assets/plugins/pagebuilder/pagebuilder.php\';\n\n$e = &$modx->event;\n\nswitch ($e->name) {\n    case \'OnWebPageInit\':\n    case \'OnManagerPageInit\': {\n        $modx->db->query(\"DELETE FROM \" . $modx->getFullTableName(\'site_plugin_events\') . \"\n            WHERE pluginid IN (\n               SELECT id\n               FROM \" . $modx->getFullTableName(\'site_plugins\') . \"\n               WHERE name = \'\" . $e->activePlugin . \"\'\n               AND disabled = 0\n            )\n            AND evtid IN (\n               SELECT id\n               FROM \" . $modx->getFullTableName(\'system_eventnames\') . \"\n               WHERE name IN (\'OnWebPageInit\', \'OnManagerPageInit\')\n            )\");\n\n        $modx->clearCache(\'full\');\n\n        (new PageBuilder($modx))->install();\n\n        return;\n    }\n\n    case \'OnDocFormRender\': {\n        $e->output((new PageBuilder($modx))->renderForm());\n        return;\n    }\n\n    case \'OnDocFormSave\': {\n        (new PageBuilder($modx))->save();\n        return;\n    }\n\n    case \'OnBeforeEmptyTrash\': {\n        (new PageBuilder($modx))->delete();\n        return;\n    }\n\n    case \'OnDocDuplicate\': {\n        (new PageBuilder($modx))->duplicate();\n        return;\n    }\n}\n\n\n','1','{\n  \"tabName\": [\n    {\n      \"label\": \"Tab name\",\n      \"type\": \"text\",\n      \"value\": \"Page Builder\",\n      \"default\": \"Page Builder\",\n      \"desc\": \"\"\n    }\n  ],\n  \"addType\": [\n    {\n      \"label\": \"Add type\",\n      \"type\": \"menu\",\n      \"value\": \"icons\",\n      \"options\": \"dropdown,icons,images\",\n      \"default\": \"dropdown\",\n      \"desc\": \"\"\n    }\n  ],\n  \"placement\": [\n    {\n      \"label\": \"Placement\",\n      \"type\": \"menu\",\n      \"value\": \"content\",\n      \"options\": \"content,tab\",\n      \"default\": \"tab\",\n      \"desc\": \"\"\n    }\n  ]\n}','0',' ','0','1531851199');
INSERT INTO `widok_evo_site_plugins` VALUES ('69','minifyHtml','<b>1.1</b> HtmlInLine plugin for MODX Evo','0','14','0','/** \n * HtmlInLine \n * \n * HtmlInLine plugin for MODX Evo\n *\n * @category    plugin \n * @version     1.1 \n * @license     http://www.gnu.org/copyleft/gpl.html GNU Public License (GPL) \n * @internal    @properties \n * @internal    @events OnWebPagePrerender \n * @internal    @modx_category Core \n * @internal    @legacy_names HtmlInLine\n * @internal    @installset base\n */\n//author Borisov Evgeniy aka Agel Nash (agel-nash@xaker.ru)\n\n$e =&$modx->event;\nswitch ($e->name) {\n    case \"OnWebPagePrerender\":{\n        if($modx->documentObject[\'searchable\']==1){\n            $content = $modx->documentOutput;\n            $content= preg_replace(\'|\\s+|\', \' \', $content);\n            $modx->documentOutput = $content;\n        }\n        break;\n    }\n}','0','{}','1',' ','0','1520370580');
INSERT INTO `widok_evo_site_plugins` VALUES ('71','TreeNodesCustomized','For EVO 1.3.0 and up','0','4','0','$e = &$modx->Event;\nswitch($e->name){\n	case \'OnManagerNodePrerender\':\n		\n		// authors taxonomy\n		if (\n			$ph[\'id\'] == \'300\'\n		) {\n			$ph[\'icon\'] = \"<i class=\'fa fa-group\'></i>\";\n			$ph[\'icon_folder_open\'] = \"<i class=\'fa fa-group\'></i>\";\n			$ph[\'icon_folder_close\'] = \"<i class=\'fa fa-group\'></i>\";\n			$ph[\'tree_page_click\'] = \'index.php?a=3&id=\'.$ph[\'id\'].\'&tab=1&sort=pagetitle&dir=ASC\';\n		}\n		\n		// change icon for context\n		if (\n			$ph[\'template\'] == \'39\' \n		) {\n			$ph[\'icon\'] = \"<i class=\'fa fa-globe\'></i>\";\n			$ph[\'icon_folder_open\'] = \"<i class=\'fa fa-globe\'></i>\";\n			$ph[\'icon_folder_close\'] = \"<i class=\'fa fa-globe\'></i>\";\n		}\n		\n		// hide child documents and open list on click\n		/*\n		if(\n			$ph[\'id\'] == \'244\'\n		) {\n			$ph[\'showChildren\'] = \'0\';\n			$ph[\'tree_page_click\'] = \'index.php?a=3&id=\'.$ph[\'id\'].\'&tab=1\';\n		}\n		*/\n		\n		$e->output(serialize($ph));\n		\n		break;\n\n	case \'OnManagerNodeRender\':\n\n		// add button to \"Create resource\" in folder \n		// on the right side of the tree node\n			if (!empty($isfolder)) {\n		//$node = \'<div class=\"tree-node-addon tree-node-addon--create\"><a href=\"?a=4&pid=\' . $id . \'&tab=0\" target=\"main\" data-roles=\"\"><i class=\"fa fa-plus-square\"></i></a></div>\' . $node;\n		//$e->setOutput($node);\n			}\n		\n		break;\n}','1','{}','0',' ','0','1551954184');
INSERT INTO `widok_evo_site_plugins` VALUES ('124','PageBuilder_saveContent','Saves PageBuilder values into site_content table','0','4','0','// PageBuilder_saveContent\n// Saves a copy of PageBuilder values into site_content table\n// Event: OnDocFormSave\n// https://gist.github.com/pmfx/fcb50c22cb83aa29c63986ce88c7db3f\n\n$e = &$modx->Event;\n\nif ( $e->name == \"OnDocFormSave\" ) {\n  \n  if ( !empty($_POST[\'contentblocks\']) && !empty($_POST[\'id\']) ) {\n    $id = (int)$_POST[\'id\'];\n    $table = $modx->getFullTableName(\'site_content\'); \n    $pb_content = \'\';\n		\n    foreach ($_POST[\'contentblocks\'] as $container => $blocks) {\n      if (is_array($blocks)) {\n        foreach ($blocks as $index => $row) {\n          $pb_content .= $modx->db->escape($row[\'values\']);\n        }\n      }\n    }\n    \n    $fields = array(\'content\' => $pb_content);\n    $result = $modx->db->update($fields, $table, \'id = \"\' . $id . \'\"\'); \n  }\n}','1','','0',' ','0','1552166692');
INSERT INTO `widok_evo_site_plugins` VALUES ('72','DashboardModifyDefault','For EVO 1.3.0 and up','0','27','0','$e = &$modx->Event;\nswitch($e->name){\n	case \'OnManagerWelcomeHome\':\n\n		if($_SESSION[\'mgrRole\']!==\'1\') {\n			//$widgets[\'onlineinfo\'][\'hide\']=\'1\';\n			$widgets[\'news\'][\'hide\']=\'1\';\n			$widgets[\'security\'][\'hide\']=\'1\';\n		}\n		//$widgets[\'recentinfo\'][\'hide\']=\'1\';\n		$widgets[\'welcome\'][\'hide\']=\'1\';\n		\n		$widgets[\'recentinfo\'][\'menuindex\']=\'100\';\n		$widgets[\'onlineinfo\'][\'menuindex\']=\'101\';\n		$widgets[\'news\'][\'menuindex\']=\'200\';\n		$widgets[\'security\'][\'menuindex\']=\'201\';\n\n		$widgets[\'onlineinfo\'][\'cols\']=\'col-sm-12\';\n\n		$e->output(serialize($widgets));\n		break;\n}','0','{}','0',' ','0','1547626522');
INSERT INTO `widok_evo_site_plugins` VALUES ('99','ManagerManager','<strong>0.6.3</strong> Customize the EVO Manager to offer bespoke admin functions for end users or manipulate the display of document fields in the manager.','0','4','0','\n/**\n * ManagerManager\n *\n * Customize the EVO Manager to offer bespoke admin functions for end users or manipulate the display of document fields in the manager.\n *\n * @category plugin\n * @version 0.6.3\n * @license http://creativecommons.org/licenses/GPL/2.0/ GNU Public License (GPL v2)\n * @internal @properties &remove_deprecated_tv_types_pref=Remove deprecated TV types;list;yes,no;yes &config_chunk=Configuration Chunk;text;mm_rules\n * @internal @events OnDocFormRender,OnDocFormPrerender,OnBeforeDocFormSave,OnDocFormSave,OnDocDuplicate,OnPluginFormRender,OnTVFormRender\n * @internal @modx_category Manager and Admin\n * @internal @installset base\n * @internal @legacy_names Image TV Preview, Show Image TVs\n * @reportissues https://github.com/DivanDesign/MODXEvo.plugin.ManagerManager/\n * @documentation README [+site_url+]assets/plugins/managermanager/readme.html\n * @documentation Official docs http://code.divandesign.biz/modx/managermanager\n * @link        Latest version http://code.divandesign.biz/modx/managermanager\n * @link        Additional tools http://code.divandesign.biz/modx\n * @link        Full changelog http://code.divandesign.biz/modx/managermanager/changelog\n * @author      Inspired by: HideEditor plugin by Timon Reinhard and Gildas; HideManagerFields by Brett @ The Man Can!\n * @author      DivanDesign studio http://www.DivanDesign.biz\n * @author      Nick Crossland http://www.rckt.co.uk\n * @author      Many others\n * @lastupdate  22/01/2018\n */\n\n// Run the main code\ninclude($modx->config[\'base_path\'].\'assets/plugins/managermanager/mm.inc.php\');\n','0','{\"remove_deprecated_tv_types_pref\":[{\"label\":\"Remove deprecated TV types\",\"type\":\"list\",\"value\":\"no\",\"options\":\"yes,no\",\"default\":\"yes\",\"desc\":\"\"}],\"config_chunk\":[{\"label\":\"Configuration Chunk\",\"type\":\"text\",\"value\":\"mm_rules\",\"default\":\"mm_rules\",\"desc\":\"\"}]}','0',' ','0','1524914668');
INSERT INTO `widok_evo_site_plugins` VALUES ('109','FastImage','<b>0.9</b> FastImageTV Helper','0','14','0','/**\n * FastImage\n * \n * FastImageTV Helper\n *\n * @category    plugin\n * @version     0.9\n * @license     http://www.gnu.org/copyleft/gpl.html GNU Public License (GPL)\n * @author      Pathologic (m@xim.name)\n * @internal    @properties &keepLost=Сколько часов хранить непривязанные файлы;text;24\n * @internal    @events OnManagerPageInit,OnDocFormSave,OnDocDuplicate,OnEmptyTrash\n * @internal    @installset base\n */\n\nrequire MODX_BASE_PATH.\'assets/tvs/FastImage/plugin.FastImage.php\';\n','0','{\n  \"keepLost\": [\n    {\n      \"label\": \"How many hours to keep not used files\",\n      \"type\": \"text\",\n      \"value\": \"1\",\n      \"default\": \"24\",\n      \"desc\": \"\"\n    }\n  ]\n}','0',' ','1523619460','1523630493');
INSERT INTO `widok_evo_site_plugins` VALUES ('111','TreeNodesExpanded','Expands selected containers by default','0','4','0','// TreeExpand\n// Expands selected containers by default\n// Author: 64j https://github.com/64j\n\n$e = &$modx->Event;\nif ($e->name == \'OnManagerPreFrameLoader\') {\n  if (empty($_SESSION[\'mgrUsrConfigSet\'][\'openedArray\'])) {\n    $_SESSION[\'openedArray\'] .= \'|\' . $treeExpandItems;\n    $_SESSION[\'mgrUsrConfigSet\'][\'openedArray\'] = $_SESSION[\'openedArray\'];\n  }\n}','0','{\n  \"treeExpandItems\": [\n    {\n      \"label\": \"Conteiners to expand\",\n      \"type\": \"text\",\n      \"value\": \"1|58|253|254\",\n      \"default\": \"\",\n      \"desc\": \"Pipe | separated list of containers IDs to be expanded by default\"\n    }\n  ]\n}','0',' ','1524684175','1551535962');
INSERT INTO `widok_evo_site_plugins` VALUES ('121','getPdf','','0','69','0','switch($_GET[\'q\']){     \n	case \'getPdf\': \n		echo $modx->runSnippet(\'getPdf\',[\n			\'getPdf\'=>\'1\'\n		]); \n		die(); \n		break;\n}','0','','1',' ','1549024515','1549025201');
INSERT INTO `widok_evo_site_plugins` VALUES ('122','ResourceFieldsDefaults','Set default values for resource fields','0','4','0','$e = &$modx->Event;\nswitch($e->name){\n	case \'OnDocFormRender\':\n		\n		global $content;\n		\n		$output = \'<script>\';\n		if (\n			$content[\'template\'] == 40 ||\n			$content[\'template\'] == 45 ||\n			$content[\'template\'] == 46\n		) {\n			$output .= \'\n			jQuery(\"[name=isfolder]\").val(\"1\");\n			jQuery(\"[name=isfoldercheck]\").prop(\"checked\", true);\n			\';\n		}\n		\n		$output .= \'</script>\';\n\n		$e->output($output);\n		break;\n}','0','{\n  \"treeExpandItems\": [\n    {\n      \"label\": \"Conteiners to expand\",\n      \"type\": \"text\",\n      \"value\": \"1|58|253|254\",\n      \"default\": \"\",\n      \"desc\": \"Pipe | separated list of containers IDs to be expanded by default\"\n    }\n  ]\n}','0',' ','0','1551009002');
INSERT INTO `widok_evo_site_plugins` VALUES ('123','ManagerCssMultiTV','Customize Evo Manager look','0','4','0','$e = & $modx->Event;\n\nif ( $e->name == \"OnManagerMainFrameHeaderHTMLBlock\" ) {\n	$html = \'\n	<style>\n		.multitv .list li.element .mtvThumb {\n			position: absolute;\n			z-index: 10;\n			right: 20px;\n			margin-bottom: 0; \n			margin-right: 0 !important;\n			width:  100px !important; \n			height: 30px !important; \n			border: 1px solid rgba(0,0,0,0.075);\n			background-color: rgba(0,0,0,0.02);\n			transition: all 0.3s ease;\n			transform-origin: center right;\n		}\n			\n		.multitv .list li.element .mtvThumb:not(:empty):hover {\n			z-index: 100;\n			width:  100px !important;\n			height: 100px !important; \n			background-color: #fff;\n			transform: scale(2) translate3d(0,-15%,0);\n		}\n				\n		.multitv .list li.element .mtvThumb img {\n			position: absolute;\n			top: 0;\n			bottom: 0;\n			left: 0;\n			right: 0;\n			width:  100% !important;\n			height: 100% !important;\n			object-fit: contain;\n			object-position: center;\n			float: none !important;\n			min-height: 1px !important;\n			min-width: 1px !important;\n			max-height: none !important;\n			max-width: none !important;\n		}\n		\n		/* show order number in table row in dataTable mode */\n		\n		.multitv .dataTable th.handle {\n			width: 50px !important;\n		}\n		\n		.multitv table.display td.handle {\n			text-indent: 15px !important;\n			background-position: 5px center !important;\n		}\n	</style>\n	\';\n	$e->output($html);\n}','0','','0',' ','0','1551535929');
INSERT INTO `widok_evo_site_plugins` VALUES ('118','OutdatedExtrasCheck','<strong>1.4.6</strong> Check for Outdated critical extras not compatible with EVO 1.4.6','0','4','0','/**\n * OutdatedExtrasCheck\n *\n * Check for Outdated critical extras not compatible with EVO 1.4.6\n *\n * @category	plugin\n * @version     1.4.6\n * @license 	http://www.gnu.org/copyleft/gpl.html GNU Public License (GPL)\n * @package     evo\n * @author      Author: Nicola Lambathakis\n * @internal    @events OnManagerWelcomeHome\n * @internal    @properties &wdgVisibility=Show widget for:;menu;All,AdminOnly,AdminExcluded,ThisRoleOnly,ThisUserOnly;AdminOnly &ThisRole=Run only for this role:;string;;;(role id) &ThisUser=Run only for this user:;string;;;(username)\n * @internal    @modx_category Manager and Admin\n * @internal    @installset base\n * @internal    @disabled 0\n */\n\nrequire MODX_BASE_PATH . \'assets/plugins/extrascheck/OutdatedExtrasCheck.plugin.php\';\n','0','{\"wdgVisibility\":[{\"label\":\"Show widget for:\",\"type\":\"menu\",\"value\":\"All\",\"options\":\"All,AdminOnly,AdminExcluded,ThisRoleOnly,ThisUserOnly\",\"default\":\"All\",\"desc\":\"\"}],\"ThisRole\":[{\"label\":\"Run only for this role:\",\"type\":\"string\",\"value\":\"\",\"default\":\"\",\"desc\":\"\"}],\"ThisUser\":[{\"label\":\"Run only for this user:\",\"type\":\"string\",\"value\":\"\",\"default\":\"\",\"desc\":\"\"}],\"DittoVersion\":[{\"label\":\"Min Ditto version:\",\"type\":\"string\",\"value\":\"2.1.3\",\"default\":\"2.1.3\",\"desc\":\"\"}],\"EformVersion\":[{\"label\":\"Min eForm version:\",\"type\":\"string\",\"value\":\"1.4.9\",\"default\":\"1.4.9\",\"desc\":\"\"}],\"AjaxSearchVersion\":[{\"label\":\"Min AjaxSearch version:\",\"type\":\"string\",\"value\":\"1.11.0\",\"default\":\"1.11.0\",\"desc\":\"\"}],\"WayfinderVersion\":[{\"label\":\"Min Wayfinder version:\",\"type\":\"string\",\"value\":\"2.0.5\",\"default\":\"2.0.5\",\"desc\":\"\"}],\"WebLoginVersion\":[{\"label\":\"Min WebLogin version:\",\"type\":\"string\",\"value\":\"1.2\",\"default\":\"1.2\",\"desc\":\"\"}],\"WebSignupVersion\":[{\"label\":\"Min WebSignup version:\",\"type\":\"string\",\"value\":\"1.1.2\",\"default\":\"1.1.2\",\"desc\":\"\"}],\"WebChangePwdVersion\":[{\"label\":\"Min WebChangePwd version:\",\"type\":\"string\",\"value\":\"1.1.2\",\"default\":\"1.1.2\",\"desc\":\"\"}],\"BreadcrumbsVersion\":[{\"label\":\"Min Breadcrumbs version:\",\"type\":\"string\",\"value\":\"1.0.5\",\"default\":\"1.0.5\",\"desc\":\"\"}],\"ReflectVersion\":[{\"label\":\"Min Reflect version:\",\"type\":\"string\",\"value\":\"2.2\",\"default\":\"2.2\",\"desc\":\"\"}],\"JotVersion\":[{\"label\":\"Min Jot version:\",\"type\":\"string\",\"value\":\"1.1.5\",\"default\":\"1.1.5\",\"desc\":\"\"}],\"MtvVersion\":[{\"label\":\"Min multiTV version:\",\"type\":\"string\",\"value\":\"2.0.12\",\"default\":\"2.0.12\",\"desc\":\"\"}],\"badthemes\":[{\"label\":\"Outdated Manager Themes:\",\"type\":\"string\",\"value\":\"MODxRE2_DropdownMenu,MODxRE2,MODxRE,MODxCarbon,D3X,MODxFLAT,wMOD,ScienceStyle\",\"default\":\"MODxRE2_DropdownMenu,MODxRE2,MODxRE,MODxCarbon,D3X,MODxFLAT,wMOD,ScienceStyle\",\"desc\":\"\"}]}','0',' ','0','1544610208');
