INSERT INTO `widok_evo_site_snippets` VALUES ('54','if','<strong>1.3</strong> A simple conditional snippet. Allows for eq/neq/lt/gt/etc logic within templates, resources, chunks, etc.','0','41','0','return require MODX_BASE_PATH.\'assets/snippets/if/snippet.if.php\';','0','{\"\":[{\"label\":\"\",\"type\":\"\",\"value\":\"\",\"default\":\"\",\"desc\":\"\"}]}',' ','0','0','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('11','siteConfig','Placeholders with config options','0','28','0','\n/*\n *  Core\n */\n	\n$hostname = \'widok.stronazen.pl\'; // should be manualy updated when moving site\n$base_url = \'http://www.\'.$hostname.\'/\';\n$tplPath  = \'assets/templates/widok_tpl/\';\n\n$currentDocId = $modx->documentIdentifier;\n$parentDocId  = $modx->runSnippet(\'tv\',[\'tv\' => \'parent\', \'id\' => $currentDocId]);\n$parentParentDocId  = $modx->runSnippet(\'tv\',[\'tv\' => \'parent\', \'id\' => $parentDocId]);\n$root_lvl_2   = $modx->runSnippet(\'UltimateParent\',array(\'topLevel\' => \'2\'));\n$level        = $modx->runSnippet(\'Level\');\n$template     = $modx->runSnippet(\'tv\',[\'tv\' => \'template\', \'id\' => $currentDocId]);\n$getId        = $modx->db->escape($_GET[\'id\']);\n$root         = $modx->runSnippet(\'UltimateParent\');\n\nif ( $root == 0 || $root == 250 ) {\n	if ( stripos($_SERVER[\'REQUEST_URI\'], \'/en/\') ) {\n		$root = 1;\n	}\n	else {\n		$root = 58;\n	}\n}\n\n$modx->setPlaceholder(\'cfg_host\',$hostname);\n$modx->setPlaceholder(\'cfg_tpl_path\',$tplPath);\n$modx->setPlaceholder(\'cfg_url_base\',$base_url);\n$modx->setPlaceholder(\'cfg_root\',$root);\n$modx->setPlaceholder(\'cfg_root_lvl_2\',$root_lvl_2);\n$modx->setPlaceholder(\'cfg_level\',$level);\n$modx->setPlaceholder(\'cfg_geturl_id\',$getId);\n\n/*\n *  Context\n */\n\n$context = $modx->getPageInfo($root);\n$context = $context[\'alias\'];\n$modx->setPlaceholder(\'context\',$context);\n$modx->setPlaceholder(\'cfg_lang\',$context);\n$modx->runSnippet(\'siteContext_\'.$context, [\'context\' => $context, \'base_url\' => $base_url]); // context settings\n\n/*\n *  Lexicon\n */\n\n$modx->runSnippet(\'siteLexicon_\'.$context); // context lexicon\n\n/*\n * Taxonomy\n */\n\n$modx->setPlaceholder(\'cfg_id_taxonomy_issue_sections\',\'263\');\n\n/*\n *  Meta Title\n */\n\n$meta_title_pagetitle = $modx->runSnippet(\'tv\',[\'tv\' => \'pagetitle\', \'id\' => $currentDocId]);\n$meta_title_custom    = $modx->runSnippet(\'tv\',[\'tv\' => \'meta_custom_title\', \'id\' => $currentDocId]);\n$meta_title_sitename  = $modx->getPlaceholder(\'lexicon.siteName\');\nif ( $meta_title_custom ) {\n	$meta_title_output = $meta_title_custom;\n}\nelse {\n	switch ( $template ) {\n		case 3  : \n			// home\n			$meta_title_output = $meta_title_sitename; \n			break;\n		case 40 : \n			// issue\n			$meta_title_lex_numberShort  = $modx->getPlaceholder(\'lexicon.numberShort\');\n			$meta_title_issueNumber      = $modx->runSnippet(\'tv\',[\'tv\' => \'issueNumber\', \'id\' => $meta_title_id]);\n			$meta_title_issueYear        = $modx->runSnippet(\'tv\',[\'tv\' => \'issueYear\',   \'id\' => $meta_title_id]);\n			$meta_title_issueTitle       = $modx->runSnippet(\'tv\',[\'tv\' => \'issueTitle\',  \'id\' => $meta_title_id]);\n			if ( $meta_title_issueTitle ) {\n				$meta_title_issueTitleColon = \': \';\n			}\n			$meta_title_output = $meta_title_lex_numberShort . \' \' . $meta_title_issueNumber . \' (\' . $meta_title_issueYear . \')\' . $meta_title_issueTitleColon.$meta_title_issueTitle .\' | \'. $meta_title_sitename; \n			break;\n		case 42 : \n			$meta_title_output = $modx->runSnippet(\'tv\',[\'tv\' => \'pagetitle\',  \'id\' => $getId]);\n			$meta_title_output = $meta_title_output .\' | \'. $meta_title_sitename; \n			;\n			break;\n		default : $meta_title_output = $meta_title_pagetitle .\' | \'. $meta_title_sitename;\n	}\n}\n$meta_title_output_esc = preg_replace(\'/&amp;(#[0-9]+|[a-z]+);/i\', \'&$1;\', htmlspecialchars($meta_title_output, ENT_QUOTES, $modx->config[\'modx_charset\']));\n$meta_title_output_esc = str_replace(array(\'[\', \']\', \'`\'),array(\'&#91;\', \'&#93;\', \'&#96;\'),$meta_title_output_esc);\n$modx->setPlaceholder(\'cfg_meta_title\',$meta_title_output);\n$modx->setPlaceholder(\'cfg_meta_title_esc\',$meta_title_output_esc);\n\n/*\n * Meta Image\n */\n\n$meta_img = $modx->runSnippet(\'tv\',[\'tv\' => \'meta_img_fitv\', \'id\' => $currentDocId]);\nif ( empty($meta_img) ) {\n	$meta_img = \'og-image-1200x630.jpg\';\n}\nelse {\n	$meta_img = $modx->runSnippet(\'phpthumb\',[\n		\'input\' => $meta_img, \n		\'options\' => \'w=1200,h=630,zc=1,q=90,f=jpg,aol=1\'\n	]);\n}\n$modx->setPlaceholder(\'cfg_meta_img\',$base_url.$meta_img);\n\n/*\n * Latest issue\n */\n\n$latestIssueId     = $modx->runSnippet(\'tv\',[\'tv\' => \'issueLatestSelector\', \'id\' => $root]);\n$latestIssueNumber = $modx->runSnippet(\'tv\',[\'tv\' => \'issueNumber\', \'id\' => $latestIssueId]);\n$latestIssueColor  = $modx->runSnippet(\'tv\',[\'tv\' => \'issueColor\', \'id\' => $latestIssueId]);\n$latestIssueCover  = $modx->runSnippet(\'tv\',[\'tv\' => \'issueCoverImage\', \'id\' => $latestIssueId]);\n$latestIssueUrl    = $modx->makeUrl($latestIssueId);\n$modx->setPlaceholder( \'cfg_latestIssue_id\',     $latestIssueId );\n$modx->setPlaceholder( \'cfg_latestIssue_number\', $latestIssueNumber );\n$modx->setPlaceholder( \'cfg_latestIssue_color\',  $latestIssueColor );\n$modx->setPlaceholder( \'cfg_latestIssue_cover\',  $latestIssueCover );\n$modx->setPlaceholder( \'cfg_latestIssue_url\',    $latestIssueUrl );\n\n/*\n * This issue color\n */\n\nswitch ($template) {\n	case 17 :\n	case 43 : $thisIssueColor = $modx->runSnippet(\'tv\',[\'tv\' => \'issueColor\', \'id\' => $parentParentDocId]);  break;\n	case 40 : $thisIssueColor = $modx->runSnippet(\'tv\',[\'tv\' => \'issueColor\', \'id\' => $currentDocId]); break;\n}\nswitch ($template) {\n	case 17 : \n	case 43 : $thisIssueid = $modx->runSnippet(\'tv\',[\'tv\' => \'id\', \'id\' => $parentParentDocId]);  break;\n	case 40 : $thisIssueid = $modx->runSnippet(\'tv\',[\'tv\' => \'id\', \'id\' => $currentDocId]); break;\n}\n$modx->setPlaceholder( \'cfg_thisIssue_color\', $thisIssueColor );\n$modx->setPlaceholder( \'cfg_thisIssue_id\',    $thisIssueid );\n\n/*\n *  Minify CSS and JS\n */\n\n$modx->runSnippet(\'MinifyCSS\',array(\n	\'files\'  => \n	  $tplPath.\'css/styles.css\',\n	\'folder\' => $tplPath.\'minifyx/\',\n	\'minify\' => \'0\'\n));\n\n$modx->runSnippet(\'MinifyJS\',array(\n	\'files\'  => \n  /*\n	$tplPath.\'js/vendor/modernizr-custom.js,\'.\n  $tplPath.\'js/vendor/bootstrap/transition.js,\'.\n  $tplPath.\'js/vendor/bootstrap/collapse.js,\'.\n  $tplPath.\'js/vendor/bootstrap/dropdown.js,\'.\n  $tplPath.\'js/vendor/debouncedresize.js,\'.\n  $tplPath.\'js/vendor/scrolloverflow.js,\'.\n  $tplPath.\'js/vendor/fullpage.js,\'.\n  $tplPath.\'js/vendor/fullpage.extensions.min.js,\'.\n  $tplPath.\'js/vendor/jquery.jplayer.js,\'.\n  //$tplPath.\'js/vendor/jquery.scrollify.js,\'.\n  $tplPath.\'js/vendor/lodash.custom.js,\'.\n  $tplPath.\'js/vendor/ofi.browser.js,\'.\n  $tplPath.\'js/vendor/owl.carousel.js,\'.\n  $tplPath.\'js/vendor/picturefill.js,\'.\n  $tplPath.\'js/vendor/selectric.js,\'.\n  $tplPath.\'js/vendor/simple-lightbox.js,\'.\n  $tplPath.\'js/app/app.js,\'.\n  $tplPath.\'js/app/app-audioplayer.js,\'.\n  $tplPath.\'js/app/app-fullpage.js,\'.\n  $tplPath.\'js/app/app-issue-color.js,\'.\n  $tplPath.\'js/app/app-objectFitPolyfill.js,\'.\n  $tplPath.\'js/app/app-owl.js,\'.\n  $tplPath.\'js/app/app-preloader.js,\'.\n  $tplPath.\'js/app/app-presentation.js,\'.\n  $tplPath.\'js/app/app-selectric.js,\'.\n  $tplPath.\'js/app/app-simple-lightbox.js,\'.\n  $tplPath.\'js/app/app-siteHeader.js\',\n  //$tplPath.\'js/app/app-scrollify.js,\'.\n  */\n	$tplPath.\'js/bundle.js\',\n	\'folder\' => $tplPath.\'minifyx/\',\n	\'minify\' => \'0\'\n));\n\n/*\n *  Register CSS, JS and HTML\n */\n\n$modx->runSnippet(\'siteRegClient\',array(\n	\'tplPath\' => $tplPath,\n	\'docTpl\'  => $template,\n	\'context\' => $context\n));\n\nreturn;','1','{\n  \"snippetConfig\": {}\n}',' ','0','1551539944','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('150','filter_options_years','','0','57','0','\n/*\n[[filter_options_years?\n&id=`filter_years`\n&parents=`[+cfg_id_news+]`\n&filterTv=`documentDate`\n&active_option=`[[GET?&v=`year`]]`\n]]\n*/\n	\n$params[\'id\']=$id;\n$params[\'parents\']=$parents;\n$params[\'showParent\']=\'0\';\n$params[\'showNoPublish\']=\'0\';\n$params[\'depth\']=\'2\';\n$params[\'orderBy\']=$filterTv.\' DESC\';\n$params[\'dateSource\']=\'tv.\'.$filterTv;\n$params[\'dateFormat\']=\'%Y\';\n$params[\'tvList\']=$filterTv;\n$params[\'renderTV\'] = $filterTv;\n$params[\'tvSortType\']=\'TVDATETIME\';\n$params[\'customLang\']=\'polish-utf8\';\n\n// content templates\n\n$params[\'tpl\']=\'@CODE:[+date+],\';\n$params[\'tplLast\']=\'@CODE:[+date+]\';\n\n// generate list\n\n$output = $modx->runSnippet(\'DocLister\',$params);\n$itemsArray = array_unique(explode(\',\', $output));\n\n$output = \'\';\n\nwhile(list($key,$val)=each($itemsArray)){\n	$active = \'\';\n	if($val != \'\'){\n		if($active_option == $val){\n			$active = \' selected=\"selected\"\';\n		}\n		$output .= \'<option value=\"\'.$val.\'\"\'.$active.\'>\'.$val.\'</option>\';\n	}\n}\n\nreturn $output;','0','{}',' ','0','1528206908','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('42','page_401','','0','28','0','\n$output = $_SERVER[\'REQUEST_URI\'];\n\n$lng_en = strpos($output, \'/en/\');\n$lng_pl = strpos($output, \'/pl/\');\n\nif($lng_en){\n  $output = \'<h1>Unauthorized access</h1><p>You are <strong>unauthorized</strong> to view this page.<br /><a href=\"[~165~]\">Click here</a> to login.</p>\';\n}\nelse{\n  $output = \'<h1>Brak dostępu</h1><p>Musisz być <strong>zalogowany</strong> aby zobaczyć tę stronę.<br /><a href=\"[~177~]\">Kliknij tutaj</a> aby się zalogować.</p>\';\n}\n\nreturn $output;','1','{}',' ','0','1505603099','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('14','tv','Displaying any document TV on page','0','41','0','\n// Description: Displaying any document TV on page\n// Example    : [[tv?tv=`yourTV`&id=`yourDocID`]]\n// Author     : Piotr Matysiak / webready.pl\n\nif ($idSource==\'get\') {\n	$id= $_GET[\'id\'];\n}\n\n$output = $modx->getTemplateVarOutput($tv,$id);\n\n// if resource is unpublished it will result no value\n// so lets try getting value from unpublished\n\nif ($output == false) {\n	$output = $modx->getTemplateVarOutput($tv,$id,0);\n}\n\n$output = $output[$tv];\nreturn $output;','0','{}',' ','0','1551016149','0');
