INSERT INTO `widok_evo_site_snippets` VALUES ('103','PageBuilder','<b>1.3.0</b> output content sections for current page','0','14','0','\n/**\n * PageBuilder\n * \n * output content sections for current page\n * \n * @version     1.3.0\n * @author      mnoskov\n * @category    snippet\n * @internal    @properties\n * @internal    @modx_category Content\n * @internal    @installset base,sample\n */\n \ninclude_once MODX_BASE_PATH . \'assets/plugins/pagebuilder/pagebuilder.php\';\nreturn (new PageBuilder($modx))->render($params);','1','{}',' ','0','1521297692','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('104','srcsetinline','Image srcset with phpthumb','0','14','0','\n$options = \'w=\'.$w;\n\nif($h) {\n	$options .= \',h=\'.$h;\n}\n\nif($zc) {\n	$options .= \',zc=\'.$zc;\n}\n\nif($q) {\n	$options .= \',q=\'.$q;\n}\nelse {\n	$options .= \',q=80\';\n}\n\n$params[\'input\']   = $input;\n$params[\'options\'] = $options.\',f=jpg\';\n\n$path = $modx->runSnippet(\'phpthumb\',$params);\n\nif($mode == \'src\') {\n	$output = $path;\n}\n\nelseif($mode == \'set\') {\n	$output = $path.\' \'.$w.\'w\' ;\n}\n\nreturn $output;','0','{}',' ','0','1520336603','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('147','run_dl_issue','','0','69','0','\n// parameters\n	\nif ( empty($parents) ) {\n	$parents = $modx->documentIdentifier;\n}\n\n$params[\'id\']            = \'issue\';\n$params[\'parents\']       = $parents;\n$params[\'showParent\']    = \'0\';\n$params[\'showNoPublish\'] = \'0\';\n$params[\'depth\']         = \'0\';\n$params[\'orderBy\']       = \'menuindex ASC\';\n$params[\'customLang\']    = \'polish-utf8\';\n\n// prepare\n\n$params[\'prepare\'] = \'dl_prepare_issue\';\n\n// templates\n\n$params[\'tpl\'] = \'dl-issue-section\';\n\n// generate list\n\n$output = $modx->runSnippet(\'DocLister\',$params);\n\nreturn $output;','0','{}',' ','0','1548850633','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('157','dl_prepare_archive','','0','69','0','\n$params[\'id\']             = \'archiveIssues\';\n$params[\'parents\']        = $data[\'id\'];\n$params[\'showParent\']     = \'0\';\n$params[\'showNoPublish\']  = \'0\';\n$params[\'depth\']          = \'0\';\n$params[\'orderBy\']        = \'menuindex DESC\';\n$params[\'tvList\']         = \'issueCoverImage,issueCoverInfoShort,issueEditorSelector,issueEditorVariant,issueNumber,issueTitle,issueYear\';\n$params[\'renderTV\']       = \'issueEditors\';\n$params[\'customLang\']     = \'polish-utf8\';\n\n// prepare\n\n$params[\'prepare\'] = function($data, $modx) {\n	// title\n	if ( $data[\'tv.issueTitle\'] ) {\n		$data[\'prepared.issueTitleHtml\'] = \'<span class=\"title\">\'.$data[\'tv.issueTitle\'].\'</span>\';\n		$data[\'prepared.issueNumberColon\'] = \':\';\n	}\n	\n	// editors\n	$editors = $data[\'tv.issueEditorSelector\'];\n	$data[\'prepared.editors\'] = $modx->runSnippet(\'simpleDocs\', [\'docs\' => $editors]);\n\n	// variant\n	$data[\'prepared.variant\'] = $modx->runSnippet(\'getVariant\', [\n		\'type\' => \'editors\',\n		\'variant\' => $data[\'tv.issueEditorVariant\']\n	]);\n\n	// lexicon\n	$data[\'lexicon.issueEditors\'] = $modx->getPlaceholder(\'lexicon.issueEditors\');\n	$data[\'lexicon.coverArtwork\'] = $modx->getPlaceholder(\'lexicon.coverArtwork\');\n	$data[\'lexicon.numberShort\']  = $modx->getPlaceholder(\'lexicon.numberShort\');\n\n	// cover thumb \n	// 1x\n	$data[\'prepared.coverImage_1x\'] = $modx->runSnippet(\'phpthumb\',[\n		\'input\'   => $data[\'tv.issueCoverImage\'], \n		\'options\' => \'w=220,h=135,zc=1,q=80,f=jpg\'\n	]);\n	// 2x\n	$data[\'prepared.coverImage_2x\'] = $modx->runSnippet(\'phpthumb\',[\n		\'input\'   => $data[\'tv.issueCoverImage\'], \n		\'options\' => \'w=440,h=270,zc=1,q=80,f=jpg\'\n	]);\n	// 3x\n	$data[\'prepared.coverImage_3x\'] = $modx->runSnippet(\'phpthumb\',[\n		\'input\'   => $data[\'tv.issueCoverImage\'], \n		\'options\' => \'w=660,h=405,zc=1,q=80,f=jpg\'\n	]);\n\n	return $data;\n};\n\n// templates\n\n$params[\'tpl\'] = \'dl-archive-issue\';\n\n// output\n\n$data[\'prepared.issues\'] = $modx->runSnippet(\'DocLister\',$params);\n\nreturn $data;','0','{}',' ','0','1548847837','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('148','run_dl_search','','0','69','0','\nif ( $_GET[\'search\'] == \'true\' ) {\n\n	$context = $modx->getPlaceholder(\'context\');\n\n	// parameters\n\n	$params[\'id\']             = \'search\';\n	$params[\'parents\']        = $modx->getPlaceholder(\'cfg_id_archive\');\n	$params[\'showParent\']     = \'0\';\n	$params[\'depth\']          = \'10\';\n	$params[\'tvList\']         = \'issueArticleAuthorSelector,issueArticleDate\';\n	$params[\'renderTV\']       = \'issueArticleDate\';\n	$params[\'orderBy\']        = \'pagetitle ASC\';\n	$params[\'debug\']          = \'0\';\n	$params[\'customLang\']     = \'polish-utf8\';\n\n	// prepare\n\n	$params[\'prepare\']        = \'dl_prepare_search\';\n\n	// content templates\n\n	$params[\'noneTPL\']        = \'@CODE: <div class=\"container-fluid\"><div class=\"content-canvas\"><div class=\"content-canvas__inner\"><p>Nic nie znaleziono :(</p></div></div></div>\';\n	$params[\'ownerTPL\']       = \'dl-search-wrap\';\n	$params[\'tpl\']            = \'dl-search-item\';\n	$params[\'noneWrapOuter\']  = \'0\';\n\n	// filters\n\n	$filters  = \'\';\n\n	// remove issues (containers)\n	//$filters .= \'content:c.template:!=:40;content:c.isfolder:!=:1\';\n	if ( !empty($_GET[\'title\']) )  $filters .= (empty($filters) ? \'\' : \';\') . \'content:longtitle:like:\'.$modx->db->escape($_GET[\'title\']);\n	if ( !empty($_GET[\'author\']) ) $filters .= (empty($filters) ? \'\' : \';\') . \'tv:issueArticleAuthorSelector:containsOne:\'.$modx->db->escape($_GET[\'author\']);\n	if ( !empty($_GET[\'content\']) ) $filters .= (empty($filters) ? \'\' : \';\') . \'content:content:like:\'.$modx->db->escape($_GET[\'content\']);\n	\n	// dateFrom\n	if ( !empty($_GET[\'dateFromYear\']) ) {\n		$dateFromYear  = $modx->db->escape($_GET[\'dateFromYear\']);\n		$dateFromMonth = $modx->db->escape($_GET[\'dateFromMonth\']);\n		$dateFromDay   = $modx->db->escape($_GET[\'dateFromDay\']);\n		if ( empty($_GET[\'dateFromMonth\']) ) {\n			$dateFromMonth = \'01\';\n		}\n		if ( empty($_GET[\'dateFromDay\']) ) {\n			$dateFromDay = \'01\';\n		}\n		$dateFrom = $dateFromYear.\'-\'.$dateFromMonth.\'-\'.$dateFromDay;\n		$filters .= (empty($filters) ? \'\' : \';\') . \'tvdate:issueArticleDate:>:\'.$dateFrom;\n	}\n	\n	// dateTo\n	if ( !empty($_GET[\'dateToYear\']) ) {\n		$dateToYear  = $modx->db->escape($_GET[\'dateToYear\']);\n		$dateToMonth = $modx->db->escape($_GET[\'dateToMonth\']);\n		$dateToDay   = $modx->db->escape($_GET[\'dateToDay\']);\n		if ( empty($_GET[\'dateToMonth\']) ) {\n			$dateToMonth = \'01\';\n		}\n		if ( empty($_GET[\'dateToDay\']) ) {\n			$dateToDay = \'01\';\n		}\n		$dateTo = $dateToYear.\'-\'.$dateToMonth.\'-\'.$dateToDay;\n		$filters .= (empty($filters) ? \'\' : \';\') . \'tvdate:issueArticleDate:<:\'.$dateTo;\n	}\n	\n	$params[\'filters\'] = \'AND(\'.$filters.\')\';\n\n	// begin output\n\n	$output = \'\';\n\n	// generate list\n\n	$output .= $modx->runSnippet(\'DocLister\',$params);\n\n	return $output;\n}','0','{}',' ','0','1552156083','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('107','run_dl_archive','','0','69','0','\n// parameters\n\n$params[\'id\']             = \'archive\';\n$params[\'parents\']        = $modx->documentIdentifier;\n$params[\'showParent\']     = \'0\';\n$params[\'showNoPublish\']  = \'0\';\n$params[\'depth\']          = \'0\';\n$params[\'orderBy\']        = \'pagetitle DESC\';\n$params[\'customLang\']     = \'polish-utf8\';\n\n// prepare\n\n$params[\'prepare\'] = \'dl_prepare_archive\';\n\n// content templates\n\n$params[\'ownerTPL\'] = \'dl-archive-wrap\';\n$params[\'tpl\']      = \'dl-archive-year\';\n\n// generate list\n\n$output = $modx->runSnippet(\'DocLister\',$params);\n\nreturn $output;','0','{}',' ','0','1548846782','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('158','dl_prepare_search','','0','69','0','\n$issueId = $data[\'parent\'];\n$issueId = $modx->runSnippet(\'tv\', [\'tv\' => \'parent\', \'id\' => $issueId]);\n\n$issueData = $modx->getTemplateVarOutput(\n	array(\n		\'pagetitle\',\n		\'issueEditorSelector\'\n	), \n	$docid=$issueId, \n	$published=1\n);\n\n$data[\'prepared.issueUrl\']     = $modx->makeUrl($issueId);\n$data[\'prepared.issueTitle\']   = $issueData[\'pagetitle\'];\n$data[\'prepared.issueEditors\'] = $modx->runSnippet(\'simpleDocs\', [ \'docs\' => $issueData[\'issueEditorSelector\'] ]);\n\nreturn $data;','0','{}',' ','0','1552142773','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('149','filter_options','','0','57','0','\n/*\n[[filter_options?\n&id=`filter_example_1`\n&parents=`[+cfg_id_taxonomy_filter_example_1+]`\n&ignoreIds=`1,2,3`\n&optionTextSource=`context`\n&includeEmptyOption=`true`\n&active_option=`[[GET?&v=`filter_example_1`]]`\n&context=`[+context+]`\n]]\n*/\n\n$params[\'id\']=$id;\n$params[\'parents\']=$parents;\n$params[\'depth\']=\'1\';\n$params[\'tvList\']=\'name_context_pl,name_context_en\';\n$params[\'showPublishedOnly\']=\'1\';\n$params[\'hideFolders\']=\'1\';\n$params[\'paginate\']=\'0\';\n$params[\'orderBy\']=\'pagetitle ASC\';\n\n// ignore ids\n\nif (!empty($ignoreIds)) {$params[\'filters\'] = \'AND(content:c.id:notin:\'.$ignoreIds.\')\';}\n\n// option text method\n\nif ($optionTextSource == \'context\') {$optionTextSourceValue = \'[+tv.name_context_\'.$context.\'+]\';}\nelse {$optionTextSourceValue = \'[+pagetitle+]\';}\n\n// active option\n	\nif ($active_option) {$active_option = $modx->db->escape($active_option);}\nelse {$active_option = \'\';}\n\nif ( $includeEmptyOption == \'true\' ) {$emptyOptionTpl  = \'<option value=\"\"></option>\';}\nelse {$emptyOptionTpl = \'\';}\n\n// template\n\n$itemTpl = \'<option value=\"[+id+]\"[+id:eq=`\'.$active_option.\'`:then=` selected`+]>\'.$optionTextSourceValue.\'</option>\';\n$params[\'tplFirst\']=\'@CODE:\'.$emptyOptionTpl.$itemTpl;\n$params[\'tpl\']=\'@CODE:\'.$itemTpl;\n\n// output\n\n$output  = \'\';\n$output .= $modx->runSnippet(\'DocLister\',$params);\n\nreturn $output;','0','{}',' ','1528199300','1552142282','0');
