INSERT INTO `widok_evo_site_snippets` VALUES ('155','simpleDocs','','0','69','0','\n// [[simpleDocs? &docs=`1,2,3` &renderAs=`links`]]\n	\n$context     = $modx->getPlaceholder(\'context\');\n$bioPage_id  = $modx->getPlaceholder(\'cfg_id_bioPage\');\n$bioPage_url = $modx->makeUrl($bioPage_id);\n\nif ( !empty($docs) ) {\n\n	$docs = explode(\',\', $docs);\n	$i = 0;\n	$len = count($docs);\n	$output = \'\';\n  \n	while (list($key,$val)=each($docs)) {\n		\n		$docTitle = $modx->getPageInfo($val);\n		$docTitle = $docTitle[\'pagetitle\'];\n		$docUrl   = $modx->makeUrl($val);\n		\n		switch ($renderAs) {\n			case \'links\'    : $tpl = \'<a href=\"\'.$docUrl.\'\">\'.$docTitle.\'</a>\'; break;\n			case \'biolinks\' : $tpl = \'<a href=\"\'.$bioPage_url.\'?id=\'.$val.\'\">\'.$docTitle.\'</a>\'; break;\n			default         : $tpl = $docTitle;\n		}\n		\n		if ($i == $len - 1) {\n			$output .= $tpl;\n		} else {\n			$output .= $tpl.\', \';\n		}\n		\n		$i++;\n		\n	}\n\n	return $output;\n}','0','{}',' ','0','1538041711','0');
