INSERT INTO `widok_evo_site_snippets` VALUES ('145','getVimeoId','Returns Vimeo movie ID from given URL','0','41','0','\n$output = \'\';\nif(preg_match(\"/(https?:\\/\\/)?(www\\.)?(player\\.)?vimeo\\.com\\/([a-z]*\\/)*([0-9]{6,11})[?]?.*/\", $url, $output_array)) {\n	$output = $output_array[5];\n}\nreturn $output;','0','{}',' ','0','1523045709','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('159','headerCircleHtml','','0','69','0','\n$currentDocId = $modx->documentIdentifier;\n$parentDocId  = $modx->runSnippet(\'tv\',[\'tv\' => \'parent\', \'id\' => $currentDocId]);\n$parentParentDocId = $modx->runSnippet(\'tv\',[\'tv\' => \'parent\', \'id\' => $parentDocId]);\n$template     = $modx->runSnippet(\'tv\',[\'tv\' => \'template\', \'id\' => $currentDocId]);\n$root         = $modx->runSnippet(\'UltimateParent\');\n\nif ( $root == 0 || $root == 250 ) {\n	if ( stripos($_SERVER[\'REQUEST_URI\'], \'/en/\') ) {\n		$root = 1;\n	}\n	else {\n		$root = 58;\n	}\n}\n\n// get latest issue data\n\n$latestIssueId     = $modx->runSnippet(\'tv\',[\'tv\' => \'issueLatestSelector\', \'id\' => $root]);\n$latestIssueNumber = $modx->runSnippet(\'tv\',[\'tv\' => \'issueNumber\', \'id\' => $latestIssueId]);\n$latestIssueUrl    = $modx->makeUrl($latestIssueId);\n\n// get selected issue data\n\n$selected_issue_id     = $modx->getPlaceholder(\'cfg_storedIssue_id\');\n$selected_issue_number = $modx->getPlaceholder(\'cfg_storedIssue_number\');\n$selected_issue_url    = $modx->getPlaceholder(\'cfg_storedIssue_url\');\n\nif ( $selected_issue_number ) {\n	$headerIssueLink   = $selected_issue_url; \n	$headerIssueNumber = $selected_issue_number; \n}\n\nelse {\n	switch ($template) {\n\n		case 17 :\n		case 43 :\n			$headerIssueLink   = $modx->makeUrl($parentParentDocId); \n			$headerIssueNumber = $modx->runSnippet(\'tv\',[\'tv\' => \'issueNumber\', \'id\' => $parentParentDocId]);  \n			break;\n\n		case 40 : \n			$headerIssueLink   = $modx->makeUrl($currentDocId); \n			$headerIssueNumber = $modx->runSnippet(\'tv\',[\'tv\' => \'issueNumber\', \'id\' => $currentDocId]);  \n			break;\n\n		default : \n			$headerIssueLink   = $latestIssueUrl; \n			$headerIssueNumber = $latestIssueNumber;\n\n	}\n}\n\n$output = \'<a class=\"c-headerissue__number\" href=\"\' . $headerIssueLink . \'#toc\">\' . $headerIssueNumber . \'</a>\';\n\nreturn $output;','0','{}',' ','1538037781','1548850485','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('160','storedIssueData','','0','69','0','\n$selected_issue_color  = $_COOKIE[\'selected_issue_color\'];\n$selected_issue_id     = $_COOKIE[\'selected_issue_id\'];\n\n// latest issue\n$root = $modx->runSnippet(\'UltimateParent\');\n\nif ( $root == 0 || $root == 250 ) {\n	if ( stripos($_SERVER[\'REQUEST_URI\'], \'/en/\') ) {\n		$root = 1;\n	}\n	else {\n		$root = 58;\n	}\n}\n\n$latestIssueId     = $modx->runSnippet(\'tv\',[\'tv\' => \'issueLatestSelector\', \'id\' => $root]);\n$latestIssueNumber = $modx->runSnippet(\'tv\',[\'tv\' => \'issueNumber\', \'id\' => $latestIssueId]);\n$latestIssueColor  = $modx->runSnippet(\'tv\',[\'tv\' => \'issueColor\', \'id\' => $latestIssueId]);\n$latestIssueCover  = $modx->runSnippet(\'tv\',[\'tv\' => \'issueCoverImage\', \'id\' => $latestIssueId]);\n$latestIssueUrl    = $modx->makeUrl($latestIssueId);\n\nif ($selected_issue_color) {\n	$modx->setPlaceholder( \'cfg_storedIssue_color\',  $selected_issue_color );\n	$modx->setPlaceholder( \'cfg_storedIssue_id\',     $selected_issue_id );\n	$modx->setPlaceholder( \'cfg_storedIssue_url\',    $modx->makeUrl( $selected_issue_id ) );\n	$modx->setPlaceholder( \'cfg_storedIssue_number\', $modx->runSnippet(\'tv\',[\'tv\' => \'issueNumber\', \'id\' => $selected_issue_id]) );\n}\nelse {\n	$modx->setPlaceholder( \'cfg_storedIssue_color\',  $latestIssueColor );\n	$modx->setPlaceholder( \'cfg_storedIssue_id\',     $latestIssueId );\n	$modx->setPlaceholder( \'cfg_storedIssue_url\',    $latestIssueUrl );\n	$modx->setPlaceholder( \'cfg_storedIssue_number\', $latestIssueNumber );\n}\n\nreturn;','0','{}',' ','1542546630','1548849888','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('166','dl_prepare_issue','','0','69','0','\n// parameters\n\n$params[\'id\']             = \'issueSection\'.$data[\'id\'].\'Articles\';\n$params[\'parents\']        = $data[\'id\'];\n$params[\'showParent\']     = \'0\';\n$params[\'showNoPublish\']  = \'0\';\n$params[\'depth\']          = \'0\';\n$params[\'orderBy\']        = \'menuindex ASC\';\n$params[\'tvList\']         = \'issueArticleAuthorSelector,issueArticleAuthorVariant,issueArticleSection,issueArticleSummary,translationSelector\';\n$params[\'renderTV\']       = \'issueArticleAuthor\';\n$params[\'customLang\']     = \'polish-utf8\';\n\n// prepare\n\n$params[\'prepare\'] = function($data, $modx) {\n	\n	// get context\n	\n	$context = $modx->getPlaceholder(\'context\');\n\n	// variant\n	\n	$data[\'prepared.variant\'] = $modx->runSnippet(\'getVariant\', [\n		\'type\' => \'authors\',\n		\'variant\' => $data[\'tv.issueArticleAuthorVariant\']\n	]);\n	\n	// translation\n	\n	$translationId = $data[\'tv.translationSelector\'];\n	\n	// title\n\n	if ( !empty($data[\'longtitle\']) ) {\n		$data[\'prepared.title\'] = $data[\'longtitle\'];\n	} \n	else {\n		$data[\'prepared.title\'] = $data[\'pagetitle\'];\n	}\n	\n	// article redirect\n	\n	if ( $data[\'template\'] == 47 ) {\n		$data[\'prepared.itemClass\'] = \'c-issue-toc__article c-issue-toc__article--no-translation\';\n		\n		// authors from translation\n		$authors = $modx->runSnippet(\'tv\', [ \n			\'tv\' => \'issueArticleAuthorSelector\', \n			\'id\' => $data[\'tv.translationSelector\'] \n		]);\n		\n		// summary from translation\n		if ( $data[\'tv.issueArticleSummary\'] ) {\n			$data[\'prepared.summary\'] = $data[\'tv.issueArticleSummary\'];\n		} \n		else {\n			$data[\'prepared.summary\'] = $modx->runSnippet(\'tv\', [ \n				\'tv\' => \'issueArticleSummary\', \n				\'id\' => $data[\'tv.translationSelector\'] \n			]);\n		}\n		\n		// url to translation\n		$data[\'prepared.url\'] = $modx->makeUrl($data[\'tv.translationSelector\']);\n	} \n	else {\n		$data[\'prepared.itemClass\'] = \'c-issue-toc__article c-issue-toc__article--has-translation\';\n		\n		// authors from current resource\n		$authors = $data[\'tv.issueArticleAuthorSelector\'];\n		if ( !empty($authors) ) {\n			$data[\'prepared.authors\'] = $modx->runSnippet(\'simpleDocs\', [\'docs\' => $authors, \'renderAs\' => \'biolinks\']);\n		}\n		\n		// summary from current resource\n		$data[\'prepared.summary\'] = $data[\'tv.issueArticleSummary\'];\n		\n		// url to this resource\n		$data[\'prepared.url\'] = $data[\'url\'];\n	}\n	\n	if ( !empty($authors) ) {\n		$data[\'prepared.authors\'] = $modx->runSnippet(\'simpleDocs\', [\'docs\' => $authors, \'renderAs\' => \'biolinks\']);\n	}\n	\n	// summary html\n	\n	if ($data[\'prepared.summary\']) {\n		$data[\'prepared.summary_html\'] = \'\n			<a class=\"c-issue-toc__article-collapse-btn\" role=\"button\" data-toggle=\"collapse\" href=\"#collapseSummary\' . $data[\'id\'] . \'\" aria-expanded=\"false\" aria-controls=\"collapseSummary\' . $data[\'id\'] . \'\" data-lexicon-show=\"[+lexicon.articleSummaryToggleShow+]\" data-lexicon-hide=\"[+lexicon.articleSummaryToggleHide+]\">\n				[+lexicon.articleSummaryToggleText+]\n			</a>\n			<div class=\"c-issue-toc__article-collapse collapse\" id=\"collapseSummary\' . $data[\'id\'] . \'\">\n				<div class=\"c-issue-toc__article-collapse-inner\">\n					\' . $data[\'prepared.summary\'] . \'\n				</div>\n			</div>\n		\';\n	}\n	else {\n		$data[\'prepared.summary_html\'] = \'\';\n	}\n\n	return $data;\n};\n\n// templates\n\n$params[\'ownerTPL\'] = \'@CODE: <ol class=\"c-issue-toc__list\">[+dl.wrap+]</ol>\';\n$params[\'tpl\']      = \'dl-issue-article\';\n\n// output\n\n$data[\'prepared.articles\'] = $modx->runSnippet(\'DocLister\',$params);\n\nreturn $data;','0','{}',' ','0','1551717169','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('161','getPdf','','0','69','0','\nif ( $_GET[\'getPdf\'] == 1 ) \n{\n	//////////////////////\n	// get config data\n	//////////////////////\n	\n	$modx->runSnippet(\'siteConfig\');\n	\n	//////////////////////\n	// resource data \n	//////////////////////\n	\n	$page_id             = $modx->documentIdentifier;\n	$page_info           = $modx->getPageInfo($page_id);\n	$page_title          = $modx->runSnippet(\'tv\', [ \'tv\' => \'pagetitle\', \'id\' => $page_id ]);\n	$page_longtitle      = $modx->runSnippet(\'tv\', [ \'tv\' => \'longtitle\', \'id\' => $page_id ]);\n	$page_author_variant = $modx->runSnippet(\'tv\', [ \'tv\' => \'issueArticleAuthorVariant\', \'id\' => $page_id ]);\n	$page_author_variant = $modx->runSnippet(\'getVariant\', [ \'type\' => \'authors\', \'variant\' => $page_author_variant ]);\n	$page_author         = $modx->runSnippet(\'tv\', [ \'tv\' => \'issueArticleAuthorSelector\', \'id\' => $page_id ]);\n	$page_author         = $modx->runSnippet(\'simpleDocs\', [ \'renderAs\' => \'biolinks\', \'docs\' => $page_author ]);\n	$page_footnotes      = $modx->runSnippet(\'tv\', [ \'tv\' => \'issueArticleFootnotesText\', \'id\' => $page_id ]);\n	$page_bibliography   = $modx->runSnippet(\'tv\', [ \'tv\' => \'issueArticleBibliography\', \'id\' => $page_id ]);\n	\n	//////////////////////\n	// prepare contents\n	//////////////////////\n	\n	// title\n	if ( $page_longtitle ) { $pdf_title = $page_longtitle; }\n	else { $pdf_title = $page_title; }\n	$pdf_title = \'<h1 class=\"mpdf-title\">\'.$pdf_title.\'</h1>\';\n	\n	// authors\n	$pdf_author_variant = $page_author_variant;\n	$pdf_author = \'<p class=\"mpdf-authors\">\' . $page_author_variant . \': \' . $page_author . \'</p>\';\n	\n	// body\n	$pdf_body = $modx->runSnippet(\'PageBuilder\', [ \n		\'blocks\' => \'content_collapseList,content_image,content_text\' \n	] );\n	$pdf_body = $modx->parseText(\n		$pdf_body, \n		array( \n			\'prepared.figcaption_html\' => \'\' ,\n			\'prepared.caption_html\' => \'\' \n		), \n		\'[+\', \'+]\' \n	);\n\n	// footnotes\n	if ( $page_footnotes ) { $pdf_footnotes = \'<div class=\"mpdf-footnotes\">\' . $page_footnotes . \'</div>\'; }\n\n	// bibliography\n	if ( $page_bibliography ) { $pdf_bibliography = \'<div class=\"mpdf-bibliography\"><h2>\' . $modx->getPlaceholder(\'lexicon.bibliography\') . \'</h2>\' . $page_bibliography . \'</div>\'; }\n	\n	// merge\n	$pdf_content = \n		$pdf_title \n		. $pdf_author\n		. $pdf_body\n		. $pdf_footnotes\n		. $pdf_bibliography\n		;\n	\n	//////////////////////\n	// mPDF\n	//////////////////////\n	\n	require_once MODX_BASE_PATH . \'assets/snippets/mpdf/source/vendor/autoload.php\';\n	\n	$mpdf = new mPDF();\n	$mpdf->SetTitle($page_title);\n  $mpdf->SetAuthor($modx->config[\'site_name\']);\n	$mpdf->WriteHTML($pdf_content);\n	$mpdf->Output();\n	\n	exit;\n}','0','{}',' ','1548167651','1550925961','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('162','getVariant','Wyświetla odmianę np Autor, Autorka, Redaktor, Redaktorzy','0','69','0','\nif ($type == \'editors\') {\n	$lex_prefix = \'lexicon.issueEditorsVariant\';\n}\nelse {\n	$lex_prefix = \'lexicon.articleAuthorsVariant\';\n}\n\nswitch ($variant) {\n	case \'he\'    : $output = $modx->getPlaceholder($lex_prefix.\'He\'); break;\n	case \'she\'   : $output = $modx->getPlaceholder($lex_prefix.\'She\'); break;\n	case \'he_x\'  : $output = $modx->getPlaceholder($lex_prefix.\'HeX\'); break;\n	case \'she_x\' : $output = $modx->getPlaceholder($lex_prefix.\'SheX\'); break;\n	default      : $output = $modx->getPlaceholder($lex_prefix.\'HeX\');\n}\n\nreturn $output;','0','{}',' ','0','1551011931','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('168','mpdfTest','','0','69','0','\nrequire_once MODX_BASE_PATH . \'assets/snippets/mpdf/source/vendor/autoload.php\';\n$mpdf = new mPDF(\'c\');\n$mpdf->WriteHTML(\'<h1>Test2</h1>\');\n$mpdf->Output();\nexit;','0','{}',' ','0','1549018302','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('167','issueArticleRedirect','','0','69','0','\nreturn $modx->sendRedirect($modx->makeURL($id));','0','{}',' ','1548924650','1548924650','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('169','getTranslationVariant','Wyświetla odmianę np Autor, Autorka, Redaktor, Redaktorzy','0','69','0','\n$lex_prefix = \'lexicon.articleTranslationVariant\';\n\nswitch ($variant) {\n	case \'he\'    : $output = $modx->getPlaceholder($lex_prefix.\'He\'); break;\n	case \'she\'   : $output = $modx->getPlaceholder($lex_prefix.\'She\'); break;\n	case \'he_x\'  : $output = $modx->getPlaceholder($lex_prefix.\'HeX\'); break;\n	case \'she_x\' : $output = $modx->getPlaceholder($lex_prefix.\'SheX\'); break;\n	default      : $output = $modx->getPlaceholder($lex_prefix.\'HeX\');\n}\n\nreturn $output;','0','{}',' ','0','1551537522','0');
INSERT INTO `widok_evo_site_snippets` VALUES ('170','switchLangLink','','0','69','0','\n$output = \'\';\n	\n$currentId       = $modx->documentIdentifier;\n$currentTpl      = $modx->runSnippet(\'tv\', [\'tv\' => \'template\', \'id\' => $currentId]);\n$translationId   = $modx->runSnippet(\'tv\', [\'tv\' => \'translationSelector\', \'id\' => $currentId]);\n$switchLang_link = $modx->getPlaceholder(\'cfg_switchLang_link\');\n$switchLang_text = $modx->getPlaceholder(\'cfg_switchLang_text\');\n	\nif ( $translationId ) {\n	$translationTpl = $modx->runSnippet(\'tv\', [\'tv\' => \'template\', \'id\' => $translationId]);\n	\n	// if translation is an article redirect, redirect it to the issue toc\n	// else redirect to actual translation\n	if ( $translationTpl == \'47\' ) {\n		// get parent of article (section id)\n		$translationIssueId = $modx->runSnippet(\'tv\', [\'tv\' => \'parent\', \'id\' => $translationId]);\n		// get parent of section (issue id)\n		$translationIssueId = $modx->runSnippet(\'tv\', [\'tv\' => \'parent\', \'id\' => $translationIssueId]);\n		// generate link to issue TOC\n		$switchLang_link = $modx->makeUrl($translationIssueId);\n		$switchLang_link = $switchLang_link.\'#toc\';\n	}\n	// bio\n	elseif ( $translationTpl == \'42\' ) {\n		$getId = $_GET[\'id\'];\n		$switchLang_link = $modx->makeUrl($translationId,\'\',\'id=\' . $getId . \'\');\n	}\n	// default direct link to translation id\n	else {\n		$switchLang_link = $modx->makeUrl($translationId);\n	}\n}\n\nelse {\n	\n}\n\n$output = \'<a class=\"c-headertools__lang-link u-underline-border\" href=\"\' . $switchLang_link . \'\">\' . $switchLang_text . \'</a>\';\n\nreturn $output;','0','{}',' ','0','1552142064','0');
INSERT INTO `widok_evo_site_templates` VALUES ('3','HomePageTpl','','0','1','','0','{{block-head}}\n\n<div id=\"js-fullpage-issue\">\n\n	<div class=\"fullpage-section\">\n		<div class=\"m-issue-section m-issue-section--cover\" data-anchor=\"cover\">\n			<div class=\"c-jump2issue\">\n				<span id=\"jump2issue\" class=\"c-jump2issue__circle\"></span>\n			</div>\n			[[srcset? \n			&input=`[+cfg_latestIssue_cover+]` \n			&sizes=`100vw` \n			&srcset=`480,768,1024,1280,1920`\n			&defaultSize=`1920`\n			&quality=`80`\n			&attrAlt=`Issue cover`\n			]]\n		</div>\n	</div>\n\n	<div class=\"fullpage-section\">\n		<div class=\"m-issue-section\" data-anchor=\"toc\">\n			<div id=\"issueContents\">\n				<div class=\"container-fluid\">\n					<div class=\"content-canvas content-canvas--small\">\n						<div class=\"content-canvas__inner\">\n							<article class=\"m-issue\">\n								\n								<section>\n									<div class=\"c-issue-meta\">\n										<img class=\"c-issue-meta__img\" \n												 srcset=\"[[phpthumb? &input=`[[tv? &id=`[+cfg_latestIssue_id+]` &tv=`issueCoverImage`]]` &options=`w=660,h=405,zc=1,q=80,f=jpg`]] 3x, \n																 [[phpthumb? &input=`[[tv? &id=`[+cfg_latestIssue_id+]` &tv=`issueCoverImage`]]` &options=`w=440,h=270,zc=1,q=80,f=jpg`]] 2x, \n																 [[phpthumb? &input=`[[tv? &id=`[+cfg_latestIssue_id+]` &tv=`issueCoverImage`]]` &options=`w=220,h=135,zc=1,q=80,f=jpg`]] 1x\"\n												 src=\"[[phpthumb? &input=`[[tv? &id=`[+cfg_latestIssue_id+]` &tv=`issueCoverImage`]]` &options=`w=220,h=135,zc=1,q=80,f=jpg`]]\" \n												 alt=\"[[tv? &id=`[+cfg_latestIssue_id+]` &tv=`pagetitle`]]\">\n										<div class=\"c-issue-meta__info\">\n											<h1 class=\"c-issue-meta__title\">\n												<span class=\"combined\">\n													<span class=\"number\">[+lexicon.numberShort+] [[tv? &id=`[+cfg_latestIssue_id+]` &tv=`issueNumber`]][[if? &separator=`||` &is=`[[tv? &id=`[+cfg_latestIssue_id+]` &tv=`issueTitle`]]:!empty` &then=`: `]]</span>\n													<span class=\"title\">[[tv? &id=`[+cfg_latestIssue_id+]` &tv=`issueTitle`]]</span>\n												</span>\n											</h1>\n											<div class=\"c-issue-meta__editors\"><p><span>[[getVariant? &type=`editors` &variant=`[[tv? &id=`[+cfg_latestIssue_id+]` &tv=`issueEditorVariant`]]`]]: [[simpleDocs?&docs=`[[tv? &id=`[+cfg_latestIssue_id+]` &tv=`issueEditorSelector`]]`]]</span></p></div>\n										</div>\n										<div class=\"clearfix\" aria-hidden=\"true\"></div>\n										<div class=\"c-issue-meta__coverinfo\" data-lex-cover=\"[+lexicon.coverArtwork+]:\">[[tv? &id=`[+cfg_latestIssue_id+]` &tv=`issueCoverInfo`]]</div>\n										<div class=\"c-issue-meta__summary\">[[tv? &id=`[+cfg_latestIssue_id+]` &tv=`issueSummary`]]</div>\n									</div>\n								</section>\n\n								<section>\n									<div id=\"issue_toc\" class=\"c-issue-toc\">\n										<h2 class=\"c-issue-toc__title\"><span>[+lexicon.toc+]</span></h2>\n										[[run_dl_issue? &parents=`[+cfg_latestIssue_id+]`]]\n									</div>\n								</section>\n								\n							</article>\n						</div>\n					</div>\n				</div>\n			</div>\n		</div>\n	</div>\n\n</div>\n\n{{block-foot}}','0','1','1553109654','0');
INSERT INTO `widok_evo_site_templates` VALUES ('4','BasicPageTpl','','0','1','','0','{{block-head}}\n\n{{block-subnav}}\n\n<section>\n	<div class=\"container-fluid\">\n		<div class=\"content-canvas content-canvas--small\">\n			<div class=\"content-canvas__inner\">\n				[[PageBuilder]]\n			</div>\n		</div>\n	</div>\n</section>\n\n{{block-foot}}','0','1','1533992903','0');
INSERT INTO `widok_evo_site_templates` VALUES ('40','IssueTpl','','0','1','','0','{{block-head}}\n\n<div id=\"js-fullpage-issue\">\n\n	<div class=\"fullpage-section\">\n		<div class=\"m-issue-section m-issue-section--cover\" data-anchor=\"cover\">\n			<div class=\"c-jump2issue\">\n				<span id=\"jump2issue\" class=\"c-jump2issue__circle\"></span>\n			</div>\n			[[srcset? \n			&input=`[*issueCoverImage*]` \n			&sizes=`100vw` \n			&srcset=`480,768,1024,1280,1920`\n			&defaultSize=`1920`\n			&quality=`80`\n			&attrAlt=`[*issueCoverInfo:esc*]`\n			]]\n		</div>\n	</div>\n\n	<div class=\"fullpage-section\">\n		<div class=\"m-issue-section\" data-anchor=\"toc\">\n			<div id=\"issueContents\">\n				<div class=\"container-fluid\">\n					<div class=\"content-canvas content-canvas--small\">\n						<div class=\"content-canvas__inner\">\n							<article class=\"m-issue\">\n								\n								<section>\n									<div class=\"c-issue-meta\">\n										<img class=\"c-issue-meta__img\" \n												 srcset=\"[[phpthumb? &input=`[*issueCoverImage*]` &options=`w=660,h=405,zc=1,q=80,f=jpg`]] 3x, \n																 [[phpthumb? &input=`[*issueCoverImage*]` &options=`w=440,h=270,zc=1,q=80,f=jpg`]] 2x, \n																 [[phpthumb? &input=`[*issueCoverImage*]` &options=`w=220,h=135,zc=1,q=80,f=jpg`]] 1x\"\n												 src=\"[[phpthumb? &input=`[*issueCoverImage*]` &options=`w=220,h=135,zc=1,q=80,f=jpg`]]\"\n												 alt=\"[*issueCoverInfo:esc*]\">\n										<div class=\"c-issue-meta__info\">\n											<h1 class=\"c-issue-meta__title\">\n												<span class=\"combined\">\n													<span class=\"number\">[+lexicon.numberShort+] [*issueNumber*][[if? &separator=`||` &is=`[*issueTitle*]:!empty` &then=`: `]]</span>\n													<span class=\"title\">[*issueTitle*]</span>\n												</span>\n											</h1>\n											<div class=\"c-issue-meta__editors\"><p><span>[[getVariant? &type=`editors` &variant=`[*issueEditorVariant*]`]]: [[simpleDocs?&docs=`[*issueEditorSelector*]`]]</span></p></div>\n										</div>\n										<div class=\"clearfix\" aria-hidden=\"true\"></div>\n										<div class=\"c-issue-meta__coverinfo\" data-lex-cover=\"[+lexicon.coverArtwork+]:\">[*issueCoverInfo*]</div>\n										<div class=\"c-issue-meta__summary\">[*issueSummary*]</div>\n									</div>\n								</section>\n								\n								<section>\n									<div id=\"issue_toc\" class=\"c-issue-toc\">\n										<h2 class=\"c-issue-toc__title\"><span>[+lexicon.toc+]</span></h2>\n										[[run_dl_issue]]\n									</div>\n								</section>\n								\n							</article>\n						</div>\n					</div>\n				</div>\n			</div>\n		</div>\n	</div>\n\n</div>\n\n{{block-foot}}','0','1','1553109366','0');
INSERT INTO `widok_evo_site_templates` VALUES ('41','_author','','0','54','','0','<!DOCTYPE html>\n<html dir=\"ltr\" lang=\"pl\">\n	<head>\n		<base href=\"[(site_url)]\">\n		<meta charset=\"utf-8\">\n		<title>[[tv? &tv=`pagetitle` &id=`[+cfg_geturl_id+]`]]</title>\n		<meta name=\"robots\" content=\"noindex,nofollow\">\n		<meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n		<link rel=\"stylesheet\" href=\"https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css\" integrity=\"sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T\" crossorigin=\"anonymous\">\n	</head>\n	<body>\n		<section>\n			<div class=\"container-fluid\">\n				<div class=\"content-canvas content-canvas--small\">\n					<div class=\"content-canvas__inner\">\n\n						<div class=\"c-team\">\n\n							[[if? &is=`[[tv? &tv=`authorPicture` &id=`[+cfg_geturl_id+]`]]:!empty` &then=`@TPL:block-bio-image`]]\n\n							<div class=\"c-team__desc\">\n								<h1 class=\"c-team__title\">[[tv? &tv=`pagetitle` &id=`[+cfg_geturl_id+]`]]</h1>\n								\n								<p class=\"c-team__meta\">\n									[[multiTV?\n									&tvName=`authorAffiliation_pl_mtv`\n									&docid=`[+cfg_geturl_id+]`\n									&tplConfig=``\n									&outerTpl=`mtv-profileMeta-wrap`\n									&rowTpl=`mtv-profileMeta-item`\n									&display=`all`\n									&rows=`all`\n									&noResults=``\n									&outputSeparator=``\n									]]\n								</p>\n								\n								<p>\n									[[tv? &tv=`authorDescription_pl` &id=`[+cfg_geturl_id+]`]]\n								</p>\n								\n							</div>\n						</div>\n\n					</div>\n				</div>\n			</div>\n		</section>\n		[!evoSystemInfo?&theme=`dark`!]\n	</body>\n</html>','0','1','1551712664','0');
INSERT INTO `widok_evo_site_templates` VALUES ('8','SearchTpl','','0','1','','0','{{block-head}}\n\n<div class=\"container-fluid\">\n	<div class=\"content-canvas content-canvas--small\">\n		<div class=\"content-canvas__inner\">\n			<div class=\"m-search\">\n\n				<form action=\"\" method=\"get\">\n\n					<input type=\"hidden\" name=\"search\" value=\"true\">\n\n					<!--\n					<fieldset>\n						<div class=\"form-group\">\n							<label class=\"c-search-label c-search-label--big\"><span>Przeszukaj wszystkie kategorie</span></label>\n							<input type=\"text\" class=\"form-control\" name=\"all\" value=\"[[$_GET[\'all\']]]\">\n							<div class=\"clearfix\"></div>\n						</div>\n					</fieldset>\n					-->\n					\n					<fieldset>\n						<label class=\"c-search-label c-search-label--big\"><span>Przeszukaj pola</span></label>\n						<div class=\"inner-fieldset\">\n							<div class=\"form-group\">\n								<label class=\"c-search-label c-search-label--default\">Autor</label>\n								<select id=\"search_author\" class=\"form-control\" name=\"author\">\n									[[filter_options?\n									&id=`search_author`\n									&parents=`300`\n									&ignoreIds=``\n									&optionTextSource=`pagetitle`\n									&includeEmptyOption=`true`\n									&active_option=`[[GET?&v=`author`]]`\n									&context=`[+context+]`\n									]]\n								</select>\n                <div class=\"clearfix\"></div>\n							</div>\n							<div class=\"form-group\">\n								<label class=\"c-search-label c-search-label--default\">Tytuł</label>\n								<input type=\"text\" class=\"form-control\" name=\"title\" value=\"[[$_GET[\'title\']]]\">\n                <div class=\"clearfix\"></div>\n							</div>\n							<div class=\"form-group\">\n								<label class=\"c-search-label c-search-label--default\">Pełny tekst</label>\n								<input type=\"text\" class=\"form-control\" name=\"content\" value=\"[[$_GET[\'content\']]]\">\n                <div class=\"clearfix\"></div>\n							</div>\n							<!--\n							-->\n						</div>\n					</fieldset>\n\n					<fieldset>\n						<label class=\"c-search-label c-search-label--big\"><span>Data</span></label>\n						<div class=\"inner-fieldset\">\n							<div class=\"form-group form-group--inline form-group--daterange\">\n								<label class=\"c-search-label c-search-label--default\">Od</label>\n								<div class=\"form-inline\">\n									<select name=\"dateFromYear\" class=\"form-control\">\n										<option value=\"\"></option>\n										[[filter_options_years?\n										&id=`filter_years`\n										&parents=`[+cfg_id_archive+]`\n										&filterTv=`issueArticleDate`\n										&active_option=`[[GET?&v=`dateFromYear`]]`\n										]]\n									</select>\n									<select name=\"dateFromMonth\" class=\"form-control\">\n										<option value=\"\"></option>\n										[[filter_options_months?\n										&id=`filter_months`\n										&parents=`[+cfg_id_archive+]`\n										&date_tv=`issueArticleDate`\n										&active_option=`[[GET?&v=`dateFromMonth`]]`\n										]]\n									</select>\n									<select name=\"dateFromDay\" class=\"form-control\">\n										<option value=\"\"></option>\n										[[filter_options_days?\n										&id=`filter_days`\n										&parents=`[+cfg_id_archive+]`\n										&date_tv=`issueArticleDate`\n										&active_option=`[[GET?&v=`dateFromDay`]]`\n										]]\n									</select>\n								</div>\n                <div class=\"clearfix\"></div>\n							</div>\n							<div class=\"form-group form-group--inline form-group--daterange\">\n								<label class=\"c-search-label c-search-label--default\">Do</label>\n								<div class=\"form-inline\">\n									<select name=\"dateToYear\" class=\"form-control\">\n										<option value=\"\"></option>\n										[[filter_options_years?\n										&id=`filter_years_to`\n										&parents=`[+cfg_id_archive+]`\n										&filterTv=`issueArticleDate`\n										&active_option=`[[GET?&v=`dateToYear`]]`\n										]]\n									</select>\n									<select name=\"dateToMonth\" class=\"form-control\">\n										<option value=\"\"></option>\n										[[filter_options_months?\n										&id=`filter_months_to`\n										&parents=`[+cfg_id_archive+]`\n										&date_tv=`issueArticleDate`\n										&active_option=`[[GET?&v=`dateToMonth`]]`\n										]]\n									</select>\n									<select name=\"dateToDay\" class=\"form-control\">\n										<option value=\"\"></option>\n										[[filter_options_days?\n										&id=`filter_days_to`\n										&parents=`[+cfg_id_archive+]`\n										&date_tv=`issueArticleDate`\n										&active_option=`[[GET?&v=`dateToDay`]]`\n										]]\n									</select>\n								</div>\n                <div class=\"clearfix\"></div>\n							</div>\n						</div>\n					</fieldset>\n\n					<div class=\"form-group form-group--actions\">\n						<button class=\"btn btn-primary\" type=\"submit\">[+lexicon.searchButtonTxt+]</button>\n					</div>\n\n				</form>\n\n			</div>\n		</div>\n	</div>\n</div>\n\n[[run_dl_search]]\n\n{{block-foot}}','0','1','1552155997','0');
