INSERT INTO `widok_evo_site_templates` VALUES ('42','ProfileTpl','','0','1','','0','{{block-head}}\n\n{{block-subnav}}\n\n<section>\n	<div class=\"container-fluid\">\n		<div class=\"content-canvas content-canvas--small\">\n			<div class=\"content-canvas__inner\">\n\n				<div class=\"c-team\">\n\n					[[if? &is=`[[tv? &tv=`authorPicture` &id=`[+cfg_geturl_id+]`]]:!empty` &then=`@TPL:block-bio-image`]]\n\n					<div class=\"c-team__desc\">\n						<h1 class=\"c-team__title\">[[tv? &tv=`pagetitle` &id=`[+cfg_geturl_id+]`]]</h1>\n						<p class=\"c-team__meta\">\n							[[multiTV?\n							&tvName=`authorAffiliation_[+context+]_mtv`\n							&docid=`[+cfg_geturl_id+]`\n							&tplConfig=``\n							&outerTpl=`mtv-profileMeta-wrap`\n							&rowTpl=`mtv-profileMeta-item`\n							&display=`all`\n							&rows=`all`\n							&noResults=``\n							&outputSeparator=``\n							]]\n						</p>\n						<div class=\"u-typo\" style=\"margin-top: 2em;\">\n							[[tv? &tv=`authorDescription_[+context+]` &id=`[+cfg_geturl_id+]`]]\n						</div>\n					</div>\n				</div>\n\n			</div>\n		</div>\n	</div>\n</section>\n\n{{block-foot}}','0','1','1551534549','0');
INSERT INTO `widok_evo_site_templates` VALUES ('17','IssueArticleTpl','','0','1','','0','[!getPdf!]{{block-head}}\n\n<a class=\"c-download c-download--article-desktop\" target=\"_blank\" href=\"[~[*id*]~]?getPdf=1\"></a>\n\n<div class=\"container-fluid\">\n	<div class=\"content-canvas content-canvas--small\">\n		<div class=\"content-canvas__inner\">\n			<article class=\"m-article\">\n\n				<header class=\"m-article__header\">\n					<h1 class=\"c-article-title c-article-title--has-download\">\n						<span>[*longtitle:ifempty=`[*pagetitle*]`*]</span>\n						<a class=\"c-download c-download--article-mobile\" target=\"_blank\" href=\"[~[*id*]~]?getPdf=1\"></a>\n					</h1>\n					{{block-article-meta}}\n				</header>\n\n				[[PageBuilder]]\n\n				[[if? &separator=`||` &is=`[*issueArticleFootnotesText*]||!empty` &then=`@TPL:block-article-footnotes-text`]]\n\n				[[if? &separator=`||` &is=`[*issueArticleBibliography*]||!empty` &then=`@TPL:block-article-bibliography`]]\n\n			</article>\n		</div>\n	</div>\n</div>\n\n{{block-foot}}','0','1','1552155135','0');
