INSERT INTO `widok_evo_site_templates` VALUES ('43','IssuePresentationTpl','','0','1','','0','{{block-head}}\n\n[[if? \n&separator=`||`\n&is=`[*issuePresentationAudioFile*]||!empty` \n&then=`@TPL:block-presentation-audio`\n]]\n\n<article id=\"js-presentation\">\n\n	<section class=\"fullpage-section fullpage-section--bgc-[*issuePresentationMetaBgc*]\" data-anchor=\"start\">\n		<div class=\"m-presentation m-presentation--text m-presentation--bgc-[*issuePresentationMetaBgc*]\">\n			<div class=\"m-presentation__outer\" style=\"min-height: calc(100vh - 100px);\">\n				<div class=\"m-presentation__inner\">\n					<div class=\"container-fluid\">\n						<div class=\"content-canvas content-canvas--small\">\n							<div class=\"content-canvas__inner\">\n								<div class=\"c-article-meta\">\n									<h1 class=\"c-article-meta__title\"><span>[*longtitle:ifempty=`[*pagetitle*]`*]</span></h1>\n									{{block-article-meta}}\n								</div>\n								<div class=\"u-typo\">\n									[[if? \n									&is=`[*issuePresentationMetaType*]:=:text` \n									&then=`@TPL:block-presentation-start-text`\n									&else=`@TPL:block-presentation-start-image`\n									]]\n								</div>\n								<button type=\"button\" id=\"presentationScrollDown\" class=\"m-presentation__control m-presentation__control--scroll\"></button>\n							</div>\n						</div>\n					</div>\n				</div>\n			</div>\n		</div>\n	</section>\n\n	[[PageBuilder]]\n\n	<section class=\"fullpage-section fullpage-section--bgc-black\" data-anchor=\"end\">\n		<div class=\"m-presentation m-presentation--image m-presentation--bgc-black\">\n			<div class=\"m-presentation__outer\">\n				<div class=\"m-presentation__inner\">\n					<div class=\"container-fluid\">\n						<div class=\"content-canvas content-canvas--small\">\n							<div class=\"content-canvas__inner\">\n								\n								<button type=\"button\" id=\"presentationScrollStart\" class=\"m-presentation__control m-presentation__control--scroll-start\"></button>\n								\n							</div>\n						</div>\n					</div>\n				</div>\n			</div>\n		</div>\n	</section>\n\n</article>\n\n{{block-foot}}','0','1','1553079962','0');
