INSERT INTO `widok_evo_site_tmplvar_contentvalues` VALUES ('1601','80','311','{\"fieldValue\":[{\"title\":\"Instytut Badań Literackich, Polska Akademia Nauk\"},{\"title\":\"Widok. Fundacja Kultiry Wizualnej\"}],\"fieldSettings\":{\"autoincrement\":1}}');
INSERT INTO `widok_evo_site_tmplvar_contentvalues` VALUES ('1602','66','312','<p>Ur. 1972. Wykładowczyni w Instytucie Kultury Polskiej na Uniwersytecie Warszawskim. Zajmuje się historią nowoczesnej kultury polskiej w perspektywie obrazu, antropologią kultury wizualnej oraz problematyką ciała i gender. Autorka książki <em>Twarze w tłumie</em> (2005, Nominacja do Nagrody Nike, Nagroda im. Michałka za najlepszą książkę filmoznawczą), wsp&oacute;łautorka książek <em>Obyczaje polskie. Wiek XX w kr&oacute;tkich hasłach </em>(2008), <em>Ślady Holokaustu w imaginarium kultury polskiej</em> (2017), <em>Ekspozycje nowoczesności.</em> <em>Wystawy a doświadczanie proces&oacute;w modernizacyjnych w Polsce 1821&ndash;1929 </em>(2017) oraz <em>Kultura wizualna w Polsce</em> (2017, dwa tomy), redaktorka tomu<em> Film i historia </em>(2008), wsp&oacute;łredaktorka <em>Antropologii ciała </em>(2008) oraz <em>Antropologii kultury wizualnej </em>(2012). Więcej: http://ikp.uw.edu.pl/dr-hab-iwona-kurz/</p>');
INSERT INTO `widok_evo_site_tmplvar_contentvalues` VALUES ('1603','73','312','assets/resource_images/312/profilePicture_1547486395_profileimage-7.jpg');
