INSERT INTO `widok_evo_site_tmplvar_contentvalues` VALUES ('1805','92','382','<p>Art historian, curator, producer of exhibitions. Since 2006 she works at Zachęta &mdash; National Gallery of Art in Warsaw, since 2012 as a member of its curatorial team.</p>');
