INSERT INTO `widok_evo_site_tmplvar_contentvalues` VALUES ('1810','37','367','<p>Komiks inspirowany przygotowaniami do wystawy <a href=\"https://zacheta.art.pl/pl/wystawy/plac-malachowskiego-3\" target=\"_blank\" rel=\"noopener\">&bdquo;Plac Małachowskiego 3&rdquo;</a>,&nbsp;kuratorka: Magdalena Komornicka,&nbsp;Zachęta &mdash; Narodowa Galeria Sztuki, Warszawa, 18 czerwca &ndash; 30 września 2018.</p>');
