INSERT INTO `widok_evo_site_tmplvar_contentvalues` VALUES ('1849','37','386','<p>Festiwal &bdquo;Pomada 7. Seks, kłamstwa i kasety wideo&rdquo;, kt&oacute;rego częścią była wystawa &bdquo;Dziedzictwo&rdquo;, odbyła się w dniach 29 września-1 października 2017 roku. Pomada zorganizowana została przez zesp&oacute;ł w składzie: Michał Grzegorzek, Aleksandra Knychalska, Joanna Manecka, Karol Radziszewski, Justyna Sobolak. Kuratorami wystawy &bdquo;Dziedzictwo&rdquo; byli Michał Grzegorzek, Karol Radziszewski i Wojciech Szymański.</p>');
