INSERT INTO `widok_evo_site_tmplvar_contentvalues` VALUES ('1930','91','399','{\"fieldValue\":[{\"title\":\"Center for Anthropology of Literature and Culture Studies at the Polish Studies Department, Jagiellonian University\",\"url\":\"http://www.antropologia.polonistyka.uj.edu.pl/katedra/pracownicy/dr-roma-sendyka\"},{\"title\":\"English home page\",\"url\":\"https://sites.google.com/site/romasendyka/\"}],\"fieldSettings\":{\"autoincrement\":1}}');
INSERT INTO `widok_evo_site_tmplvar_contentvalues` VALUES ('1931','92','399','<p>Lecturer at the Department of Anthropology of Literature and Cultural Research of the Jagiellonian University. She runs the Center for the Study of Cultures of Memory. She is active in the Curatorial Collective. Her fields of research include theories of literary and cultural research, in particular research on visual culture and cultures of memory. Author of books&nbsp;<em>Nowoczesny&nbsp;</em><em>esej. Studium historycznej świadomości gatunku</em>&nbsp;[Modern Essay. The Study in the Historical Consciousness of the Genres (2006)],&nbsp;<em>Od kultury ja do kultury siebie</em>&nbsp;[From the I-Culture to the Culture of the Self. On Reflexive Forms in Identity Projects (2015)], co-editor of several volumes in memory studies. In 2011, she taught at the University of Chicago. The leader of the project&nbsp;<em>Awkward Objects of Genocide</em>&nbsp;(TRACES, Horizon 2020) and&nbsp;<em>Unacknowledged Genocide Sites&nbsp;</em>(National Programme for the Development of Humanities in Poland).</p>');
INSERT INTO `widok_evo_site_tmplvar_contentvalues` VALUES ('1932','37','400','<p>An interview with Roma Sendyka (Jagiellonian University) about the activities of the Curatorial Collective.</p>');
