INSERT INTO `widok_evo_site_tmplvar_contentvalues` VALUES ('1953','92','404','<p>B. 1976. Philosopher, author of books&nbsp;<em>O&nbsp;</em>maszynach<em>. T. 1.&nbsp;</em>Wiek<em>&nbsp;XVII a&nbsp;</em>filozofia techniki [About the Machines. 17th century and philosophy of technique (2014)] and&nbsp;<em>Tw&oacute;rca&nbsp;</em>i perspektywy [Creator and perspectives, (2014)]. Lecturer at the Jagiellonian University and the School of Form in Poznań.</p>');
