INSERT INTO `widok_evo_site_tmplvar_contentvalues` VALUES ('1967','37','409','<p>Artykuł jest pr&oacute;bą szczeg&oacute;łowego om&oacute;wienia treści i aktualności eseju Waltera Benjamina Dzieło sztuki w dobie jego reprodukcji technicznej. Autorka przedstawia w nim nie tylko liczne inspiracje stojące za teoretycznymi i historycznymi wywodami Benjamina, ale także szuka wsp&oacute;łczesnych zjawisk pokazujących wagę i nowe aspekty jego rozważań.</p>');
