mm_hideFields(\'content\');
mm_hideFields(\'description\');
mm_hideFields(\'introtext\');
mm_hideFields(\'menutitle\');

// new weblink condition

if (is_array($content) && $content[\'type\'] != \'reference\') {
	mm_hideFields(\'link_attributes\');
	mm_hideFields(\'longtitle\',\'\',\'22,35,39,40,41,45,46\');
} 
else {
	mm_hideFields(\'longtitle\');
  //mm_hideFields(\'content\');
}

// new document condition

if (is_array($content) && $content[\'template\'] == \'22\') {
	mm_hideFields(\'alias\');
	mm_hideFields(\'link_attributes\');
	mm_hideFields(\'menuindex\');
	mm_hideFields(\'pagetitle\');
	mm_hideFields(\'parent\');
	mm_hideFields(\'show_in_menu\');
  //mm_hideFields(\'content\');
	mm_hideTabs(\'access,settings\');
} 
else {
  mm_moveFieldsToTab(\'show_in_menu\', \'settings\');
  mm_moveFieldsToTab(\'menuindex\', \'settings\');
  //mm_moveFieldsToTab(\'parent\', \'settings\');
  mm_moveFieldsToTab(\'alias\', \'settings\');
  //mm_moveFieldsToTab(\'template\', \'settings\');
  mm_moveFieldsToTab(\'published\', \'general\');
  //mm_moveFieldsToTab(\'content\', \'settings\');
  //mm_moveFieldsToTab(\'content\', \'general\');
}

// rename field

mm_renameField(\'alias\',\'Alias URL\');
mm_renameField(\'template\',\'Szablon\');

// default field value

mm_default(\'alias_visible\',\'0\',\'\',\'45,46\');
//mm_default(\'is_folder\',\'1\',\'\',\'40,45,46\');
mm_default(\'searchable\',\'0\',\'\',\'8,35,41\');
mm_default(\'show_in_menu\',\'0\');

// required fields

mm_requireFields(\'issueNumber,issueYear\');
mm_requireFields(\'longtitle\',\'\',\'17,43,47\');

// hide templates

mm_hideTemplates(\'3,8,13,14,15,16,25\',\'2,3\');

// widgets

mm_widget_showimagetvs(\'\',250,250); // Always give a preview of Image TVs
mm_ddMaxLength(\'pagetitle\', \'\', \'\', 70);
//mm_ddMaxLength(\'article_summary\', \'\', \'\', 150);
mm_ddMaxLength(\'meta_custom_title\', \'\', \'\', 70);
mm_ddMaxLength(\'meta_description\', \'\', \'\', 320);

// example of how PHP is allowed - check that a TV named documentTags exists before creating rule
if($modx->db->getValue("SELECT COUNT(id) FROM " . $modx->getFullTableName(\'site_tmplvars\') . " WHERE name=\'documentTags\'")) {
    mm_widget_tags(\'documentTags\',\',\'); // Give blog tag editing capabilities to the \'documentTags (3)\' TV
}
