            $output[] = \'        <priority>\'.$doc[$priority].\'</priority>\';
            $output[] = \'        <changefreq>\'.$doc[$changefreq].\'</changefreq>\';
            $output[] = \'    </url>\';
        }
        
        $output[] = \'</urlset>\';

}

return join("\\n",$output);

