    $rs = $modx->db->select(\'name\',\'[+prefix+]site_tmplvars\',sprintf("name=\'%s\'",$modx->db->escape($excludeTV)));
    $excludeTV_exists  = $modx->db->getRecordCount($rs) ? 1 : 0;
    
    // add sub-children to the list
